INSERT INTO `booking` (`id`, `address`, `appointment_date`, `appointment_time`, `conclude`, `consultation_fee`, `created_date`, `disease_description`, `dob`, `full_name`, `pay_status`, `phone`, `service_fee`, `doctor_id`, `user_id`, `end_time`, `start_time`) VALUES
(1, 'Trường chinh, Hai bà trưng, Hà nội', '2025-05-26', NULL, '<p>Kết quả tổng qu&aacute;t, bạn bị trĩ độ 2 haha</p>', 400000, '2025-05-23 22:02:59', 'tôi bị đau chân', '2000-10-02', NULL, 'DA_THANH_TOAN_DU', '0976123123', 1900000, 1, 1, '09:00:00', '08:00:00'),
(2, 'Trường chinh, Hai bà trưng, Hà nội', '2025-05-26', NULL, '<p>đ&acirc;y l&agrave; tổng kết quả&nbsp;</p>', 400000, '2025-05-24 07:16:10', 'tôi bị gãy vai', '2000-10-02', 'Trần Hiếu', 'DA_THANH_TOAN_KHAM', '0932478234', 0, 1, 7, '10:00:00', '09:00:00'),
(3, 'Trường chinh, Hai bà trưng, Hà nội', '2025-05-27', NULL, '<p>Thầy thuốc ưu t&uacute;, TS.BS Nguyễn Phạm &Yacute; Nhi l&agrave; một nh&agrave; chuy&ecirc;n m&ocirc;n, nh&agrave; quản l&yacute; y tế xuất sắc. TS.BS Nguyễn Phạm &Yacute; Nhi đ&atilde; vinh dự nhận Hu&acirc;n chương lao động hạng ba, c&ugrave;ng nhiều Bằng khen của Bộ trưởng Bộ y tế, Chủ tịch UBND th&agrave;nh phố H&agrave; Nội trao tặng.</p>\n<p>L&agrave; chuy&ecirc;n gia đầu ng&agrave;nh về tim mạch trẻ em, b&aacute;c sĩ &Yacute; Nhi từng giữ nhiều chức vụ quan trọng như: Gi&aacute;m Đốc Bệnh viện Đa khoa Xanh P&ocirc;n H&agrave; Nội, Chủ tịch Hội Nhi khoa H&agrave; Nội.</p>\n<p>Với nền tảng chuy&ecirc;n m&ocirc;n được đ&agrave;o tạo ở nhiều trường đại học nổi tiếng trong, ngo&agrave;i nước v&agrave; hơn 30 năm kinh nghiệm, b&aacute;c sĩ đ&atilde; gi&uacute;p h&agrave;ng ng&agrave;n tr&aacute;i tim của c&aacute;c em thơ lỗi nhịp đập trở lại b&igrave;nh thường.</p>\n<p>B&ecirc;n cạnh hoạt động chuy&ecirc;n m&ocirc;n, b&aacute;c sĩ &Yacute; Nhi từng l&agrave; đại biểu Quốc hội, t&iacute;ch cực đ&oacute;ng g&oacute;p tiếng n&oacute;i của m&igrave;nh về c&aacute;c vấn đề x&atilde; hội.</p>\n<p>Với bề d&agrave;y th&agrave;nh t&iacute;ch trong c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n v&agrave; quản l&yacute;, TS.BS Nguyễn Phạm &Yacute; Nhi đ&atilde; đồng h&agrave;nh v&agrave; dẫn dắt&nbsp;<a href=\"https://benhvienthucuc.vn/\">Bệnh viện Đa khoa Quốc tế Thu C&uacute;c</a> TCI đạt được nhiều th&agrave;nh tựu nổi bật tr&ecirc;n cương vị Gi&aacute;m đốc chuy&ecirc;n m&ocirc;n của Bệnh viện. B&aacute;c sĩ quản l&yacute; vận h&agrave;nh tối ưu phương ph&aacute;p hội chẩn li&ecirc;n chuy&ecirc;n khoa, chỉ đạo v&agrave; định hướng phối kết hợp hiệu quả giữa c&aacute;c chuy&ecirc;n gia, b&aacute;c sĩ, kỹ thuật vi&ecirc;n của c&aacute;c khoa trong to&agrave;n bệnh viện, trong nước v&agrave; quốc tế. Từ đ&oacute; gi&uacute;p chẩn đo&aacute;n nhanh, ch&iacute;nh x&aacute;c, kh&ocirc;ng bỏ lọt bệnh, điều trị tr&uacute;ng đ&iacute;ch, đ&uacute;ng bệnh.</p>', 400000, '2025-05-24 09:57:21', 'đây là mô tả bệnh của tôi', '2000-01-21', 'Trần văn hiếu', 'DA_THANH_TOAN_DU', '0976123123', 2300000, 1, 1, '09:00:00', '08:00:00');

