INSERT INTO `booking_detail` (`id`, `price`, `result`, `booking_id`, `services_id`) VALUES
(4, 1200000, '<p>Bạn kh&ocirc;ng bị m&aacute;u nhiễm mỡ</p>\n<p>xin ch&uacute;c mừng</p>', 1, 1),
(5, 700000, NULL, 1, 3),
(6, 1200000, '<h2>2. C&uacute;m m&ugrave;a &ndash; Bệnh dễ l&acirc;y lan, nguy cơ biến chứng cao</h2>\n<h3><span id=\"21-Benh-cum-va-co-che-lay-lan\" class=\"ez-toc-section\"></span><span id=\"21-Benh-cum-va-co-che-lay-lan\" class=\"ez-toc-section\"></span>2.1 Bệnh c&uacute;m v&agrave; cơ chế l&acirc;y lan</h3>\n<p>C&uacute;m m&ugrave;a l&agrave; một bệnh l&yacute; truyền nhiễm c&oacute; khả năng l&acirc;y lan qua đường h&ocirc; hấp, khiến bệnh nh&acirc;n dễ d&agrave;ng bị nhiễm khi tiếp x&uacute;c với người bệnh hoặc c&aacute;c dịch tiết trong kh&ocirc;ng kh&iacute;, hay tiếp x&uacute;c với c&aacute;c bề mặt nhiễm virus. Bệnh c&uacute;m nếu kh&ocirc;ng được điều trị kịp thời, đặc biệt đối với trẻ nhỏ, người cao tuổi v&agrave; những người c&oacute; bệnh l&yacute; nền c&oacute; thể nhanh ch&oacute;ng dẫn đến c&aacute;c biến chứng nghi&ecirc;m trọng như vi&ecirc;m phổi, suy h&ocirc; hấp v&agrave; thậm ch&iacute; tử vong&hellip;</p>\n<div id=\"attachment_703790\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-703790 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2025/02/co-che-lay-truyen-cum.jpg\" alt=\"C&uacute;m m&ugrave;a &ndash; Bệnh dễ l&acirc;y lan, nguy cơ biến chứng cao\" width=\"800\" height=\"550\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-703790\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2025/02/co-che-lay-truyen-cum.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-703790\" class=\"wp-caption-text\">Bệnh c&uacute;m l&acirc;y lan chủ yếu qua đường h&ocirc; hấp khi người bệnh ho, hắt hơi hoặc tiếp x&uacute;c với c&aacute;c bề mặt nhiễm virus</p>\n</div>\n<h3><span id=\"22-Cac-trieu-chung-pho-bien-va-cac-bien-chung-cua-benh-cum-mua\" class=\"ez-toc-section\"></span><span id=\"22-Cac-trieu-chung-pho-bien-va-cac-bien-chung-cua-benh-cum-mua\" class=\"ez-toc-section\"></span>2.2 C&aacute;c triệu chứng phổ biến v&agrave; c&aacute;c biến chứng của bệnh c&uacute;m m&ugrave;a</h3>', 3, 1),
(7, 400000, '<p>&ndash; Sốt cao (tr&ecirc;n 38&deg;C).</p>\n<p>&ndash;&nbsp;<a href=\"https://benhvienthucuc.vn/ho-khan-la-gi-phuong-phap-dieu-tri-ho-khan/\">Ho khan</a>, đau họng, sổ mũi.</p>\n<p>&ndash; Mệt mỏi v&agrave; đau nhức cơ thể.</p>\n<p>&ndash; Đau đầu v&agrave; đ&ocirc;i khi c&oacute; triệu chứng buồn n&ocirc;n, ti&ecirc;u chảy (đặc biệt ở trẻ em).</p>\n<div id=\"attachment_703787\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-703787 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2025/02/trieu-chung-benh-cum.jpg\" alt=\"Triệu chứng phổ biến bệnh c&uacute;m m&ugrave;a\" width=\"800\" height=\"550\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-703787\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2025/02/trieu-chung-benh-cum.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-703787\" class=\"wp-caption-text\">Khi nhận thấy c&aacute;c triệu chứng đ&aacute;ng ch&uacute; &yacute; như sốt cao, đau họng, sổ mũi, mệt mỏi&hellip; n&ecirc;n theo d&otilde;i v&agrave; thăm kh&aacute;m kiểm tra sớm</p>\n</div>\n<p>Nếu kh&ocirc;ng được điều trị kịp thời, bệnh c&uacute;m c&oacute; thể dẫn đến c&aacute;c biến chứng nghi&ecirc;m trọng, chẳng hạn như:</p>\n<p>&ndash; Vi&ecirc;m phổi: Do virus tấn c&ocirc;ng trực tiếp v&agrave;o phổi hoặc bội nhiễm vi khuẩn.</p>', 3, 2),
(8, 700000, NULL, 3, 3);

