INSERT INTO `centers` (`id`, `center_name`, `city`, `created_date`, `description`, `district`, `image`, `street`, `ward`) VALUES
(1, 'Cơ sở khám thiên thanh Hai Bà Trưng', 'Hà Nội', '2025-05-20 19:24:11', '<p><strong>Bệnh viện Quốc tế Thu C&uacute;c</strong>&nbsp;nay l&agrave; Hệ thống Y tế Thu C&uacute;c đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội.</p>\n<h2>Thời gian l&agrave;m việc Bệnh viện Quốc tế Thu C&uacute;c</h2>\n<p><strong>BỆNH VIỆN ĐA KHOA QUỐC TẾ THU C&Uacute;C</strong></p>\n<ul>\n<li>Địa Chỉ: 286 Thụy Khu&ecirc;, T&acirc;y Hồ, H&agrave; Nội</li>\n<li>Hotline Đặt lịch kh&aacute;m:&nbsp;<strong>0941298865</strong></li>\n</ul>\n<p><strong>Giờ l&agrave;m việc:</strong></p>\n<ul>\n<li>Thứ 2 - Chủ nhật:&nbsp;6h30 - 17h00&nbsp;</li>\n<li>Ph&ograve;ng kh&aacute;m Nội - đa khoa từ 6h30 - 17h00</li>\n<li>Cấp cứu: 24/24</li>\n<li>Kh&aacute;m Ung bướu: 8h00 - 17h00</li>\n<li>Kh&aacute;m Tai Mũi Họng: 8h00 &ndash; 20h00</li>\n<li>Kh&aacute;m Răng h&agrave;m mặt: 8h00 - 17h00</li>\n</ul>\n<p><strong>PH&Ograve;NG KH&Aacute;M ĐA KHOA QUỐC TẾ THU C&Uacute;C</strong></p>\n<p>Địa chỉ: 216 Trần Duy Hưng, Cầu Giấy, H&agrave; Nội</p>\n<p>Địa chỉ: 32 Đại Từ, Ho&agrave;ng Mai, H&agrave; Nội</p>\n<p>Địa chỉ: 136 Nguyễn Tr&atilde;i, Thanh Xu&acirc;n, H&agrave; Nội</p>\n<p><strong>Giờ l&agrave;m việc:</strong></p>\n<ul>\n<li>Thứ 2 - Chủ nhật:&nbsp;7h00 - 17h00</li>\n<li>Ph&ograve;ng kh&aacute;m Mắt, Da liễu: 8h00 &ndash; 17h00</li>\n</ul>\n<h2><strong>Giới thiệu về Bệnh viện Quốc tế Thu C&uacute;c</strong></h2>\n<p><strong>Bệnh viện Quốc tế Thu C&uacute;c</strong>&nbsp;nay l&agrave; Hệ thống Y tế Thu C&uacute;c th&agrave;nh lập từ năm 2011 đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội. Sắp tới đ&acirc;y, Hệ thống y tế Thu C&uacute;c sẽ thực hiện nhanh ch&oacute;ng việc mở chuỗi c&aacute;c cơ sở y tế Thu C&uacute;c tr&ecirc;n to&agrave;n quốc, đưa dịch vụ y tế chất lượng cao đến gần v&agrave; dễ tiếp cận hơn với người d&acirc;n khắp cả nước. nay l&agrave; Hệ thống Y tế Thu C&uacute;c đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội</p>\n<h2><strong>Cơ sở vật chất v&agrave; trang thiết bị y tế</strong></h2>\n<p><strong>Bệnh viện Quốc tế Thu C&uacute;c</strong>&nbsp;đầu tư cơ sở khang trang, hiện đại. Bệnh viện gồm 10 tầng với kết cấu tiện lợi, hợp l&yacute;, c&ugrave;ng với hệ thống thang m&aacute;y gi&uacute;p người bệnh dễ d&agrave;ng hơn trong việc di chuyển. Nằm s&aacute;t cạnh Hồ T&acirc;y, đ&oacute;n gi&oacute; v&agrave;o m&ugrave;a Hạ v&agrave; điều h&ograve;a độ ẩm v&agrave;o m&ugrave;a đ&ocirc;ng, Bệnh viện Đa khoa Quốc tế Thu C&uacute;c kh&ocirc;ng chỉ mang lại cảm gi&aacute;c thư th&aacute;i cho người bệnh đến kh&aacute;m bệnh m&agrave; c&ograve;n l&agrave; kh&ocirc;ng gian nghĩ dưỡng tuyệt vời cho những bệnh nh&acirc;n điều trị nội tr&uacute; tại đ&acirc;y.</p>\n<p>Ngo&agrave;i ra&nbsp;<strong>Bệnh viện Quốc tế Thu C&uacute;c</strong>&nbsp;đầu tư mạnh về trang thiết bị y tế để đ&aacute;p ứng nhu cầu kh&aacute;m v&agrave; điều trị bệnh</p>\n<ul>\n<li>Hệ thống ph&ograve;ng mổ v&ocirc; khuẩn một chiều được đ&aacute;nh gi&aacute; l&agrave; hiện đại nhất hiện nay v&agrave; rất hiếm c&oacute; tr&ecirc;n cả nước</li>\n<li>m&aacute;y chụp cắt lớp CT,</li>\n<li>m&aacute;y đo lo&atilde;ng xương DEXUMT,</li>\n<li>hệ thống si&ecirc;u &acirc;m m&agrave;u 4D,</li>\n<li>điện tim 3 cần,</li>\n<li>nội soi g&acirc;y m&ecirc;</li>\n<li>M&aacute;y chụp cắt lớp CT đa l&aacute;t cắt gi&uacute;p tầm so&aacute;t để ph&aacute;t hiện sớm bệnh l&yacute; ung thư, tầm so&aacute;t bệnh l&yacute; tim mạch</li>\n<li>M&aacute;y đo lo&atilde;ng xương d&ugrave;ng tia X hai b&igrave;nh diện DEXUM T</li>\n<li>hệ thống ph&ograve;ng lab Sinh học ph&acirc;n tử;</li>\n<li>Hệ thống ph&ograve;ng lab Vi sinh cao cấp nu&ocirc;i cấy,</li>\n<li>kh&aacute;ng sinh đồ (kể cả vi tr&ugrave;ng Helcobacter Pylori dạ d&agrave;y);</li>\n<li>Hệ thống tầm so&aacute;t vi khuẩn Helicobacter Pylori qua hơi thở</li>\n<li>MRI nguy&ecirc;n l&yacute; H2,</li>\n<li>MS CT đa d&atilde;y,</li>\n<li>si&ecirc;u &acirc;m đ&agrave;n hồi m&ocirc; gan thế hệ mới,</li>\n<li>hệ thống x&eacute;t nghiệm tự động bằng Robot</li>\n</ul>\n<h2><strong>C&aacute;c dịch vụ tại&nbsp;</strong>Bệnh viện Quốc tế Thu C&uacute;c</h2>\n<ul>\n<li>Kh&aacute;m chữa bệnh</li>\n<li>G&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t</li>\n<li>G&oacute;i tầm so&aacute;t ung thư</li>\n<li>Kh&aacute;m sức khỏe doanh nghiệp</li>\n</ul>\n<h2><strong>Ch&iacute;nh s&aacute;ch BHYT tại&nbsp;</strong>Bệnh viện Quốc tế Thu C&uacute;c</h2>\n<p>Từ giữa th&aacute;ng 07/2019, Ph&ograve;ng kh&aacute;m ĐKQT Thu C&uacute;c &aacute;p dụng ch&iacute;nh s&aacute;ch Th&ocirc;ng tuyến BHYT, theo đ&oacute; người d&acirc;n khi sở hữu Thẻ bảo hiểm y tế với nơi đăng k&iacute; kh&aacute;m chữa bệnh ban đầu ghi tr&ecirc;n thẻ l&agrave; tại c&aacute;c cơ sở y tế thuộc tuyến quận, huyện, x&atilde;, phường, y tế cơ quan, trạm x&aacute; v&agrave; c&aacute;c bệnh viện hạng 3 trong c&ugrave;ng địa b&agrave;n th&agrave;nh phố H&agrave; Nội. Ri&ecirc;ng c&aacute;c trường hợp ngoại tỉnh th&igrave; cần cung cấp giấy c&ocirc;ng t&aacute;c, giấy cử đi học, giấy tạm tr&uacute;&hellip; sẽ được hưởng đầy đủ quyền lợi tr&ecirc;n thẻ BHYT giống như kh&aacute;m đ&uacute;ng tuyến.</p>\n<h2><strong>Ch&iacute;nh s&aacute;ch bảo hiểm phi nh&acirc;n thọ tại&nbsp;</strong>Bệnh viện Quốc tế Thu C&uacute;c</h2>\n<p><strong>Danh s&aacute;ch c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ được bảo l&atilde;nh tại&nbsp;</strong><strong>Bệnh viện Quốc tế Thu C&uacute;c</strong><strong>:</strong></p>\n<p>ATACC, VBI, JLT, UIC&rsquo;s, FTC, Bakco, Care +, Care Plus, Bảo hiểm AIA, Bảo hiểm Pacific Cross, Bảo hiểm Dai &ndash; Ichi, Bảo hiểm Marsh, Bảo hiểm AON, Bảo hiểm Generali, Bảo hiểm Blue Cross, Bảo hiểm PJICO,&nbsp;Bảo hiểm Prudential, Bảo hiểm Liberty, Bảo hiểm Viễn Đ&ocirc;ng &ndash; VASS, Bảo hiểm to&agrave;n cầu &ndash; GIC, Bảo hiểm BIC, Bảo hiểm Bưu điện &ndash; PTI, Dịch vụ Bảo Hiểm Nam &Aacute;, Bảo hiểm IN SMART,&nbsp;Bảo hiểm Qu&acirc;n đội, Bảo hiểm dầu kh&iacute; &ndash; PVI, Bảo hiểm Bảo Minh, Bảo hiểm Bảo Việt</p>\n<p><strong>Thủ tục bảo l&atilde;nh tại&nbsp;</strong><strong>Bệnh viện Quốc tế Thu C&uacute;c</strong><strong>:</strong></p>\n<p><strong>Những giấy tờ cần thiết khi l&agrave;m thủ tục bảo l&atilde;nh:</strong></p>\n<ul>\n<li>Thẻ bảo hiểm</li>\n<li>Chứng minh thư nh&acirc;n d&acirc;n hoặc hộ chiếu. Giấy khai sinh nếu l&agrave; trẻ em chưa đủ 18 tuổi</li>\n</ul>\n<p><strong>Quy tr&igrave;nh bảo l&atilde;nh</strong></p>\n<ul>\n<li><strong>Quy tr&igrave;nh bảo l&atilde;nh Ngoại tr&uacute;</strong></li>\n</ul>\n<p><strong>Bước 1</strong>: Người bệnh xuất tr&igrave;nh thẻ bảo hiểm phi nh&acirc;n thọ v&agrave; bảo hiểm y tế(nếu c&oacute;) K&egrave;m chứng minh thư hoặc hộ chiếu. Giấy khai sinh nếu trẻ em dưới 18 tuổi</p>\n<p><strong>Bước 2</strong>: Lễ t&acirc;n nhập th&ocirc;ng tin kh&aacute;m bệnh v&agrave; hướng dẫn người bệnh đi kh&aacute;m</p>\n<p><strong>Bước 3</strong>: Qu&yacute; kh&aacute;ch k&yacute; h&oacute;a đơn, y&ecirc; cầu bảo l&atilde;nh. Qu&yacute; kh&aacute;ch chỉ đ&oacute;ng tiền với những khoản c&ocirc;ng ty bảo hiểm từ chối bồi thường</p>\n<ul>\n<li><strong>Quy tr&igrave;nh bảo l&atilde;nh Nội tr&uacute;</strong></li>\n</ul>\n<p><strong>Bước 1</strong>: Người bệnh xuất tr&igrave;nh thẻ bảo hiểm phi nh&acirc;n thọ, bảo hiểm y tế (nếu c&oacute;) v&agrave; giấy tờ t&ugrave;y th&acirc;n (chứng minh thư hoặc hộ chiếu, giấy khai sinh nếu trẻ em dưới 18 tuổi) trong v&ograve;ng 24h sau khi nhập viện</p>\n<p><strong>Bước 2</strong>: Bệnh viện Thu C&uacute;c gửi th&ocirc;ng tin dự kiến chi ph&iacute; điều trị sang cho c&ocirc;ng ty bảo hiểm</p>\n<p><strong>Bước 3</strong>: C&ocirc;ng ty bảo hiểm th&ocirc;ng b&aacute;o chi ph&iacute; bảo l&atilde;nh dự kiến cho bệnh viện</p>\n<p><strong>Bước 4</strong>: Bệnh viện th&ocirc;ng b&aacute;o kết quả bảo l&atilde;nh cho người bệnh</p>\n<p><strong>Bước 5</strong>: Người bệnh k&yacute; hồ sơ bảo l&atilde;nh + h&oacute;a đơn v&agrave; thanh to&aacute;n những dịch vụ c&ocirc;ng ty bảo hiểm từ chối bảo l&atilde;nh (nếu c&oacute;)</p>\n<p><strong>Bạn được hỗ trợ những g&igrave; khi kh&ocirc;ng được bảo l&atilde;nh trực tiếp</strong></p>\n<ul>\n<li>Đối với những trường hợp kh&ocirc;ng được bảo l&atilde;nh trực tiếp tại Thu C&uacute;c bao gồm cả những bảo hiểm kh&ocirc;ng k&yacute; kết thỏa thuận thanh to&aacute;n trực tiếp với bệnh viện Thu C&uacute;c hoặc quyền lợi của người bệnh kh&ocirc;ng được bảo l&atilde;nh trực tiếp. Ch&uacute;ng t&ocirc;i sẵn s&agrave;ng hỗ trợ bạn chuẩn bị đầy đủ giấy tờ để c&ocirc;ng việc thanh to&aacute;n của bạn với c&ocirc;ng ty bảo hiểm thuận lợi, nhanh ch&oacute;ng.</li>\n</ul>\n<p><strong>Những giấy tờ bệnh viện Thu C&uacute;c sẽ hỗ trợ bạn bao gồm:</strong></p>\n<p><strong>Đối với Ngoại tr&uacute;:</strong></p>\n<ul>\n<li>Sổ kh&aacute;m bệnh c&oacute; x&aacute;c nhận của bệnh viện</li>\n<li>Kết quả những dịch vụ người bệnh thực hiện c&oacute; x&aacute;c nhận của bệnh viện</li>\n<li>Đơn thuốc</li>\n<li>Phiếu thu tiền dịch vụ, thuốc</li>\n<li>H&oacute;a đơn VAT tiền dịch vụ, thuốc</li>\n<li>Bảng k&ecirc; chi tiết dịch vụ sử dụng</li>\n</ul>\n<p><strong>Đối với Nội tr&uacute;</strong></p>\n<ul>\n<li>Kết quả những dịch vụ người bệnh thực hiện c&oacute; x&aacute;c nhận của bệnh viện</li>\n<li>Đơn thuốc</li>\n<li>Phiếu thu tiền dịch vụ, thuốc</li>\n<li>H&oacute;a đơn VAT tiền dịch vụ, thuốc</li>\n<li>Bảng k&ecirc; chi tiết dịch vụ sử dụng</li>\n<li>Giấy chứng nhận phẫu thuật, giấy ra viện</li>\n</ul>', 'Hai Bà Trưng', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747743486/zans6vj1smudycnj8fpz.jpg', '28 A, trần đại nghĩa', 'Trương Định'),
(3, 'Cơ sở khám Phùng Hứng', 'Hà Nội', '2025-05-21 17:02:40', '<p>Sau hơn một thập kỷ đi v&agrave;o hoạt động, Bệnh viện Đa khoa Quốc tế Thu C&uacute;c &ndash; cơ sở ra đời đầu ti&ecirc;n của Hệ thống Y tế Thu C&uacute;c TCI đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội. Tới đ&acirc;y, Hệ thống y tế Thu C&uacute;c sẽ thực hiện nhanh ch&oacute;ng việc mở chuỗi c&aacute;c cơ sở y tế Thu C&uacute;c TCI tr&ecirc;n to&agrave;n quốc, đưa dịch vụ y tế chất lượng cao đến gần v&agrave; dễ tiếp cận hơn với người d&acirc;n cả nước. Mục ti&ecirc;u n&agrave;y c&oacute; sự hỗ trợ t&iacute;ch cực từ nguồn vốn 26,7 triệu USD được Quỹ đầu tư danh tiếng Vinacapital r&oacute;t v&agrave;o cho Hệ thống Y tế Thu C&uacute;c trong th&aacute;ng 8/2020.</p>\n<div>\n<div id=\"attachment_257939\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257939 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" alt=\"Hệ thống Y tế Thu C&uacute;c quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257939\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257939\" class=\"wp-caption-text\">Hệ thống Y tế Thu C&uacute;c TCI quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.</p>\n</div>\n</div>\n<p>Nhiều năm qua, Hệ thống Y tế Thu C&uacute;c TCI đ&atilde; tạo được uy t&iacute;n lớn về chất lượng kh&aacute;m chữa bệnh v&agrave; dịch vụ chăm s&oacute;c với cơ sở vật chất hiện đại vượt trội, trang thiết bị y tế ti&ecirc;n tiến, c&ugrave;ng đội ngũ b&aacute;c sĩ giỏi chuy&ecirc;n m&ocirc;n, gi&agrave;u y đức. Chất lượng kh&aacute;m chữa bệnh lu&ocirc;n l&agrave; ti&ecirc;u ch&iacute; được khẳng định tại Thu C&uacute;c TCI với sự đ&aacute;nh gi&aacute; cao từ Sở Y Tế H&agrave; Nội v&agrave; từ người bệnh. Kh&ocirc;ng chỉ lu&ocirc;n nằm trong Top đầu c&aacute;c bệnh viện c&oacute; điểm chất lượng cao nhất với 83 ti&ecirc;u ch&iacute; khắt khe của Sở Y tế, Bệnh viện ĐKQT Thu C&uacute;c c&ograve;n được tin chọn l&agrave; &ldquo;c&aacute;nh tay nối d&agrave;i&rdquo; của c&aacute;c bệnh viện trung ương với sứ mệnh mang đến dịch vụ y tế tin cậy cho người d&acirc;n, g&oacute;p phần giảm tải y tế c&ocirc;ng. Người bệnh lựa chọn kh&aacute;m chữa bệnh tại đ&acirc;y kh&ocirc;ng chỉ v&igrave; tin tưởng m&agrave; c&ograve;n v&igrave; h&agrave;i l&ograve;ng với phong c&aacute;ch phục vụ tận t&igrave;nh, chu đ&aacute;o. Tỷ lệ h&agrave;i l&ograve;ng khi kh&aacute;m chữa bệnh l&ecirc;n tới 99.9%.</p>\n<h2><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span>1. Hướng đi đ&uacute;ng từ những ng&agrave;y đầu th&agrave;nh lập</h2>\n<p>Từ những ng&agrave;y đầu đi v&agrave;o hoạt động, Bệnh viện ĐKQT Thu C&uacute;c l&agrave; đơn vị ti&ecirc;n phong trong hợp t&aacute;c quốc tế với c&aacute;c bệnh viện nổi tiếng tại những nước c&oacute; nền y học ph&aacute;t triển tr&ecirc;n thế giới. Nhằm thực hiện chiến lược thay đổi vị thế từ một thương hiệu được biết đến rộng r&atilde;i về thẩm mỹ sang một thương hiệu về y tế, Thu C&uacute;c TCI đ&atilde; đầu tư ph&aacute;t triển lĩnh vực kh&aacute;m chữa bệnh một c&aacute;ch b&agrave;i bản v&agrave; lựa chọn mũi nhọn ph&aacute;t triển l&agrave; những bệnh l&yacute; kh&oacute; với sự hợp t&aacute;c từ nước ngo&agrave;i. Đối với mảng ung bướu, năm 2014, Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam, đồng thời đưa đội ngũ gi&aacute;o sư, tiến sĩ giỏi nổi tiếng ở Singapore về trực tiếp thăm kh&aacute;m tại Bệnh viện ĐKQT Thu C&uacute;c. Từ đ&acirc;y, Thu C&uacute;c TCI tạo được nhận diện v&agrave; bước ngoặt mới trong lĩnh vực y tế kh&aacute;m chữa bệnh. Nhờ c&oacute; ph&aacute;c đồ điều trị chuẩn 100% Singapore v&agrave; chế độ chăm s&oacute;c to&agrave;n diện tại Bệnh viện ĐKQT Thu C&uacute;c, h&agrave;ng ng&agrave;n bệnh nh&acirc;n ung thư đ&atilde; t&igrave;m thấy cơ hội sống cho m&igrave;nh m&agrave; kh&ocirc;ng phải ra nước ngo&agrave;i.</p>\n<div>\n<div id=\"attachment_257931\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257931 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" alt=\"Thu C&uacute;c đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257931\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257931\" class=\"wp-caption-text\">Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam</p>\n</div>\n</div>\n<h2><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span>2. Tăng trưởng mạnh mẽ tạo đ&agrave; ph&aacute;t triển vượt bậc</h2>\n<p>Bước chuyển m&igrave;nh ấn tượng đ&atilde; mang đến đ&agrave; tăng trưởng mạnh mẽ trong những năm tiếp theo. Bệnh viện Đa khoa Quốc tế Thu C&uacute;c khi đ&oacute; đ&atilde; bắt tay ngay v&agrave;o giai đoạn 2 mở rộng hoạt động kinh doanh của bệnh viện. Cũng trong giai đoạn n&agrave;y, thương hiệu Thu C&uacute;c được t&aacute;ch độc lập 2 mảng hoạt động ch&iacute;nh của thương hiệu l&agrave; y tế kh&aacute;m chữa bệnh v&agrave; thẩm mỹ th&agrave;nh hai c&ocirc;ng ty ri&ecirc;ng biệt. C&ocirc;ng ty cổ phần y khoa Thu C&uacute;c bao gồm c&aacute;c bệnh viện đa khoa v&agrave; ph&ograve;ng kh&aacute;m đa khoa (Nay gọi tắt l&agrave; TCI). C&ocirc;ng ty thẩm mỹ Thu C&uacute;c được t&aacute;ch độc lập kh&ocirc;ng li&ecirc;n quan đến TCI bao gồm c&aacute;c bệnh viện phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m da liễu c&ocirc;ng nghệ cao. Mục ti&ecirc;u của thương hiệu Thu C&uacute;c l&agrave; để lĩnh vực y tế kh&aacute;m chữa bệnh ph&aacute;t triển mạnh mẽ hơn nữa, mang lại lợi &iacute;ch nhiều hơn cho người bệnh.</p>\n<p>Năm 2019, Bệnh viện ĐKQT Thu C&uacute;c &ndash; số 286-294 Thụy Khu&ecirc; được mở rộng quy m&ocirc; lớn gấp 3 lần, song song với việc khai trương Ph&ograve;ng kh&aacute;m Đa khoa Quốc tế Thu C&uacute;c tại 216 Trần Duy Hưng năm 2019. Tới th&aacute;ng 8/2020, sau 2 năm t&igrave;m hiểu, Quỹ Vinacapital ch&iacute;nh thức quyết định đầu tư v&agrave;o lĩnh vực y tế của Thu C&uacute;c, kh&ocirc;ng bao gồm thẩm mỹ. Đ&acirc;y l&agrave; một trong những bước đi chiến lược của Hệ thống trong giai đoạn ph&aacute;t triển thần tốc để trở th&agrave;nh một trong những thương hiệu dẫn đầu thị trường y tế tư nh&acirc;n.</p>', 'Nam Từ Liêm', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747821394/ezzcbficthhprcxxzflj.jpg', '371 hoàng quyền', 'Phúc Xá'),
(4, 'Cơ sở khám Q1', 'TP.HCM', '2025-05-21 17:03:55', '<p>Sau hơn một thập kỷ đi v&agrave;o hoạt động, Bệnh viện Đa khoa Quốc tế Thu C&uacute;c &ndash; cơ sở ra đời đầu ti&ecirc;n của Hệ thống Y tế Thu C&uacute;c TCI đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội. Tới đ&acirc;y, Hệ thống y tế Thu C&uacute;c sẽ thực hiện nhanh ch&oacute;ng việc mở chuỗi c&aacute;c cơ sở y tế Thu C&uacute;c TCI tr&ecirc;n to&agrave;n quốc, đưa dịch vụ y tế chất lượng cao đến gần v&agrave; dễ tiếp cận hơn với người d&acirc;n cả nước. Mục ti&ecirc;u n&agrave;y c&oacute; sự hỗ trợ t&iacute;ch cực từ nguồn vốn 26,7 triệu USD được Quỹ đầu tư danh tiếng Vinacapital r&oacute;t v&agrave;o cho Hệ thống Y tế Thu C&uacute;c trong th&aacute;ng 8/2020.</p>\n<div>\n<div id=\"attachment_257939\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257939 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" alt=\"Hệ thống Y tế Thu C&uacute;c quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257939\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257939\" class=\"wp-caption-text\">Hệ thống Y tế Thu C&uacute;c TCI quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.</p>\n</div>\n</div>\n<p>Nhiều năm qua, Hệ thống Y tế Thu C&uacute;c TCI đ&atilde; tạo được uy t&iacute;n lớn về chất lượng kh&aacute;m chữa bệnh v&agrave; dịch vụ chăm s&oacute;c với cơ sở vật chất hiện đại vượt trội, trang thiết bị y tế ti&ecirc;n tiến, c&ugrave;ng đội ngũ b&aacute;c sĩ giỏi chuy&ecirc;n m&ocirc;n, gi&agrave;u y đức. Chất lượng kh&aacute;m chữa bệnh lu&ocirc;n l&agrave; ti&ecirc;u ch&iacute; được khẳng định tại Thu C&uacute;c TCI với sự đ&aacute;nh gi&aacute; cao từ Sở Y Tế H&agrave; Nội v&agrave; từ người bệnh. Kh&ocirc;ng chỉ lu&ocirc;n nằm trong Top đầu c&aacute;c bệnh viện c&oacute; điểm chất lượng cao nhất với 83 ti&ecirc;u ch&iacute; khắt khe của Sở Y tế, Bệnh viện ĐKQT Thu C&uacute;c c&ograve;n được tin chọn l&agrave; &ldquo;c&aacute;nh tay nối d&agrave;i&rdquo; của c&aacute;c bệnh viện trung ương với sứ mệnh mang đến dịch vụ y tế tin cậy cho người d&acirc;n, g&oacute;p phần giảm tải y tế c&ocirc;ng. Người bệnh lựa chọn kh&aacute;m chữa bệnh tại đ&acirc;y kh&ocirc;ng chỉ v&igrave; tin tưởng m&agrave; c&ograve;n v&igrave; h&agrave;i l&ograve;ng với phong c&aacute;ch phục vụ tận t&igrave;nh, chu đ&aacute;o. Tỷ lệ h&agrave;i l&ograve;ng khi kh&aacute;m chữa bệnh l&ecirc;n tới 99.9%.</p>\n<h2><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span>1. Hướng đi đ&uacute;ng từ những ng&agrave;y đầu th&agrave;nh lập</h2>\n<p>Từ những ng&agrave;y đầu đi v&agrave;o hoạt động, Bệnh viện ĐKQT Thu C&uacute;c l&agrave; đơn vị ti&ecirc;n phong trong hợp t&aacute;c quốc tế với c&aacute;c bệnh viện nổi tiếng tại những nước c&oacute; nền y học ph&aacute;t triển tr&ecirc;n thế giới. Nhằm thực hiện chiến lược thay đổi vị thế từ một thương hiệu được biết đến rộng r&atilde;i về thẩm mỹ sang một thương hiệu về y tế, Thu C&uacute;c TCI đ&atilde; đầu tư ph&aacute;t triển lĩnh vực kh&aacute;m chữa bệnh một c&aacute;ch b&agrave;i bản v&agrave; lựa chọn mũi nhọn ph&aacute;t triển l&agrave; những bệnh l&yacute; kh&oacute; với sự hợp t&aacute;c từ nước ngo&agrave;i. Đối với mảng ung bướu, năm 2014, Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam, đồng thời đưa đội ngũ gi&aacute;o sư, tiến sĩ giỏi nổi tiếng ở Singapore về trực tiếp thăm kh&aacute;m tại Bệnh viện ĐKQT Thu C&uacute;c. Từ đ&acirc;y, Thu C&uacute;c TCI tạo được nhận diện v&agrave; bước ngoặt mới trong lĩnh vực y tế kh&aacute;m chữa bệnh. Nhờ c&oacute; ph&aacute;c đồ điều trị chuẩn 100% Singapore v&agrave; chế độ chăm s&oacute;c to&agrave;n diện tại Bệnh viện ĐKQT Thu C&uacute;c, h&agrave;ng ng&agrave;n bệnh nh&acirc;n ung thư đ&atilde; t&igrave;m thấy cơ hội sống cho m&igrave;nh m&agrave; kh&ocirc;ng phải ra nước ngo&agrave;i.</p>\n<div>\n<div id=\"attachment_257931\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257931 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" alt=\"Thu C&uacute;c đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257931\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257931\" class=\"wp-caption-text\">Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam</p>\n</div>\n</div>\n<h2><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span>2. Tăng trưởng mạnh mẽ tạo đ&agrave; ph&aacute;t triển vượt bậc</h2>\n<p>Bước chuyển m&igrave;nh ấn tượng đ&atilde; mang đến đ&agrave; tăng trưởng mạnh mẽ trong những năm tiếp theo. Bệnh viện Đa khoa Quốc tế Thu C&uacute;c khi đ&oacute; đ&atilde; bắt tay ngay v&agrave;o giai đoạn 2 mở rộng hoạt động kinh doanh của bệnh viện. Cũng trong giai đoạn n&agrave;y, thương hiệu Thu C&uacute;c được t&aacute;ch độc lập 2 mảng hoạt động ch&iacute;nh của thương hiệu l&agrave; y tế kh&aacute;m chữa bệnh v&agrave; thẩm mỹ th&agrave;nh hai c&ocirc;ng ty ri&ecirc;ng biệt. C&ocirc;ng ty cổ phần y khoa Thu C&uacute;c bao gồm c&aacute;c bệnh viện đa khoa v&agrave; ph&ograve;ng kh&aacute;m đa khoa (Nay gọi tắt l&agrave; TCI). C&ocirc;ng ty thẩm mỹ Thu C&uacute;c được t&aacute;ch độc lập kh&ocirc;ng li&ecirc;n quan đến TCI bao gồm c&aacute;c bệnh viện phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m da liễu c&ocirc;ng nghệ cao. Mục ti&ecirc;u của thương hiệu Thu C&uacute;c l&agrave; để lĩnh vực y tế kh&aacute;m chữa bệnh ph&aacute;t triển mạnh mẽ hơn nữa, mang lại lợi &iacute;ch nhiều hơn cho người bệnh.</p>\n<p>Năm 2019, Bệnh viện ĐKQT Thu C&uacute;c &ndash; số 286-294 Thụy Khu&ecirc; được mở rộng quy m&ocirc; lớn gấp 3 lần, song song với việc khai trương Ph&ograve;ng kh&aacute;m Đa khoa Quốc tế Thu C&uacute;c tại 216 Trần Duy Hưng năm 2019. Tới th&aacute;ng 8/2020, sau 2 năm t&igrave;m hiểu, Quỹ Vinacapital ch&iacute;nh thức quyết định đầu tư v&agrave;o lĩnh vực y tế của Thu C&uacute;c, kh&ocirc;ng bao gồm thẩm mỹ. Đ&acirc;y l&agrave; một trong những bước đi chiến lược của Hệ thống trong giai đoạn ph&aacute;t triển thần tốc để trở th&agrave;nh một trong những thương hiệu dẫn đầu thị trường y tế tư nh&acirc;n.</p>\n<div>\n<div id=\"attachment_257933\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257933 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-3-1.jpg\" alt=\"Bệnh viện ĐKQT Thu C&uacute;c l&agrave; một trong những bệnh viện tư c&oacute; số lượng bệnh nh&acirc;n đến kh&aacute;m đ&ocirc;ng nhất tại H&agrave; Nội hiện nay.\" width=\"628\" height=\"415\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257933\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-3-1.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257933\" class=\"wp-caption-text\">Bệnh viện ĐKQT Thu C&uacute;c l&agrave; một trong những bệnh viện tư c&oacute; số lượng bệnh nh&acirc;n đến kh&aacute;m đ&ocirc;ng nhất tại H&agrave; Nội hiện nay.</p>\n</div>\n</div>\n<h2><span id=\"3-Nhung-chuyen-khoa-mui-nhon\" class=\"ez-toc-section\"></span><span id=\"3-Nhung-chuyen-khoa-mui-nhon\" class=\"ez-toc-section\"></span>3. Những chuy&ecirc;n khoa mũi nhọn</h2>\n<p>Để c&oacute; được th&agrave;nh quả đ&aacute;ng tự h&agrave;o h&ocirc;m nay, chất lượng kh&aacute;m chữa bệnh v&agrave; dịch vụ chăm s&oacute;c y tế tại Thu C&uacute;c TCI đ&oacute;ng g&oacute;p phần quan trọng nhất. Hoạt động theo m&ocirc; h&igrave;nh bệnh viện đa khoa với đội ngũ b&aacute;c sĩ chuy&ecirc;n khoa giỏi, Thu C&uacute;c TCI thực hiện thăm kh&aacute;m, điều trị với đủ c&aacute;c khoa, chuy&ecirc;n khoa. Ngo&agrave;i ung bướu, c&aacute;c mảng dịch vụ kh&aacute;m chữa bệnh mũi nhọn tại Thu C&uacute;c TCI hiện nay l&agrave; sản khoa, ngoại khoa, nội gan mật,&nbsp;<a href=\"https://benhvienthucuc.vn/giai-dap-chan-doan-hinh-anh-gom-nhung-gi/\">chẩn đo&aacute;n h&igrave;nh ảnh</a>. B&ecirc;n cạnh đ&oacute; Thu Cúc TCI cũng li&ecirc;n tục mở r&ocirc;̣ng các mảng dịch vụ khác như ti&ecirc;m chủng, kính mắt,&hellip;.</p>\n<p>Với Sản khoa, những yếu tố cảm x&uacute;c trong qu&aacute; tr&igrave;nh vượt cạn để gi&uacute;p mẹ bầu c&oacute; cuộc sinh nở tốt nhất đ&atilde; được chắt lọc từ nhiều nước ph&aacute;t triển v&agrave; &aacute;p dụng một c&aacute;ch tuyệt vời tại đ&acirc;y. Lựa chọn thai sản trọn g&oacute;i ở Thu C&uacute;c TCI, c&aacute;c bậc cha mẹ đ&atilde; lựa chọn một h&agrave;nh tr&igrave;nh của sự an t&acirc;m v&agrave; hạnh ph&uacute;c để gặp thi&ecirc;n thần b&eacute; bỏng của m&igrave;nh.</p>', 'Quận 9', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747821469/ku62zhwelbzwowcrf8wl.jpg', '12/9 nguyễn chí thanh', 'Phường hoàng liệt'),
(5, 'Cơ sở khám s201', 'TP Đà Nẵng', '2025-05-21 17:05:20', '<p>Sau hơn một thập kỷ đi v&agrave;o hoạt động, Bệnh viện Đa khoa Quốc tế Thu C&uacute;c &ndash; cơ sở ra đời đầu ti&ecirc;n của Hệ thống Y tế Thu C&uacute;c TCI đang l&agrave; địa chỉ được đ&ocirc;ng đảo người bệnh tin chọn, đứng top 3 bệnh viện tư v&agrave; top 5 to&agrave;n bệnh viện c&oacute; điểm chất lượng dẫn đầu H&agrave; Nội. Tới đ&acirc;y, Hệ thống y tế Thu C&uacute;c sẽ thực hiện nhanh ch&oacute;ng việc mở chuỗi c&aacute;c cơ sở y tế Thu C&uacute;c TCI tr&ecirc;n to&agrave;n quốc, đưa dịch vụ y tế chất lượng cao đến gần v&agrave; dễ tiếp cận hơn với người d&acirc;n cả nước. Mục ti&ecirc;u n&agrave;y c&oacute; sự hỗ trợ t&iacute;ch cực từ nguồn vốn 26,7 triệu USD được Quỹ đầu tư danh tiếng Vinacapital r&oacute;t v&agrave;o cho Hệ thống Y tế Thu C&uacute;c trong th&aacute;ng 8/2020.</p>\n<div>\n<div id=\"attachment_257939\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257939 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" alt=\"Hệ thống Y tế Thu C&uacute;c quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257939\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-1-1.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257939\" class=\"wp-caption-text\">Hệ thống Y tế Thu C&uacute;c TCI quy tụ đội ngũ b&aacute;c sĩ, nh&acirc;n vi&ecirc;n y tế giỏi chuy&ecirc;n m&ocirc;n, được đ&aacute;nh gi&aacute; cao về chất lượng kh&aacute;m chữa bệnh.</p>\n</div>\n</div>\n<p>Nhiều năm qua, Hệ thống Y tế Thu C&uacute;c TCI đ&atilde; tạo được uy t&iacute;n lớn về chất lượng kh&aacute;m chữa bệnh v&agrave; dịch vụ chăm s&oacute;c với cơ sở vật chất hiện đại vượt trội, trang thiết bị y tế ti&ecirc;n tiến, c&ugrave;ng đội ngũ b&aacute;c sĩ giỏi chuy&ecirc;n m&ocirc;n, gi&agrave;u y đức. Chất lượng kh&aacute;m chữa bệnh lu&ocirc;n l&agrave; ti&ecirc;u ch&iacute; được khẳng định tại Thu C&uacute;c TCI với sự đ&aacute;nh gi&aacute; cao từ Sở Y Tế H&agrave; Nội v&agrave; từ người bệnh. Kh&ocirc;ng chỉ lu&ocirc;n nằm trong Top đầu c&aacute;c bệnh viện c&oacute; điểm chất lượng cao nhất với 83 ti&ecirc;u ch&iacute; khắt khe của Sở Y tế, Bệnh viện ĐKQT Thu C&uacute;c c&ograve;n được tin chọn l&agrave; &ldquo;c&aacute;nh tay nối d&agrave;i&rdquo; của c&aacute;c bệnh viện trung ương với sứ mệnh mang đến dịch vụ y tế tin cậy cho người d&acirc;n, g&oacute;p phần giảm tải y tế c&ocirc;ng. Người bệnh lựa chọn kh&aacute;m chữa bệnh tại đ&acirc;y kh&ocirc;ng chỉ v&igrave; tin tưởng m&agrave; c&ograve;n v&igrave; h&agrave;i l&ograve;ng với phong c&aacute;ch phục vụ tận t&igrave;nh, chu đ&aacute;o. Tỷ lệ h&agrave;i l&ograve;ng khi kh&aacute;m chữa bệnh l&ecirc;n tới 99.9%.</p>\n<h2><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span><span id=\"1-Huong-di-dung-tu-nhung-ngay-dau-thanh-lap\" class=\"ez-toc-section\"></span>1. Hướng đi đ&uacute;ng từ những ng&agrave;y đầu th&agrave;nh lập</h2>\n<p>Từ những ng&agrave;y đầu đi v&agrave;o hoạt động, Bệnh viện ĐKQT Thu C&uacute;c l&agrave; đơn vị ti&ecirc;n phong trong hợp t&aacute;c quốc tế với c&aacute;c bệnh viện nổi tiếng tại những nước c&oacute; nền y học ph&aacute;t triển tr&ecirc;n thế giới. Nhằm thực hiện chiến lược thay đổi vị thế từ một thương hiệu được biết đến rộng r&atilde;i về thẩm mỹ sang một thương hiệu về y tế, Thu C&uacute;c TCI đ&atilde; đầu tư ph&aacute;t triển lĩnh vực kh&aacute;m chữa bệnh một c&aacute;ch b&agrave;i bản v&agrave; lựa chọn mũi nhọn ph&aacute;t triển l&agrave; những bệnh l&yacute; kh&oacute; với sự hợp t&aacute;c từ nước ngo&agrave;i. Đối với mảng ung bướu, năm 2014, Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam, đồng thời đưa đội ngũ gi&aacute;o sư, tiến sĩ giỏi nổi tiếng ở Singapore về trực tiếp thăm kh&aacute;m tại Bệnh viện ĐKQT Thu C&uacute;c. Từ đ&acirc;y, Thu C&uacute;c TCI tạo được nhận diện v&agrave; bước ngoặt mới trong lĩnh vực y tế kh&aacute;m chữa bệnh. Nhờ c&oacute; ph&aacute;c đồ điều trị chuẩn 100% Singapore v&agrave; chế độ chăm s&oacute;c to&agrave;n diện tại Bệnh viện ĐKQT Thu C&uacute;c, h&agrave;ng ng&agrave;n bệnh nh&acirc;n ung thư đ&atilde; t&igrave;m thấy cơ hội sống cho m&igrave;nh m&agrave; kh&ocirc;ng phải ra nước ngo&agrave;i.</p>\n<div>\n<div id=\"attachment_257931\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257931 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" alt=\"Thu C&uacute;c đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam\" width=\"628\" height=\"419\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257931\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-2.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257931\" class=\"wp-caption-text\">Thu C&uacute;c TCI đ&atilde; hợp t&aacute;c th&agrave;nh c&ocirc;ng với Singapore, th&agrave;nh lập Trung t&acirc;m ung bướu Singapore, đưa tiến bộ y học trong điều trị ung thư ở Singapore về Việt Nam</p>\n</div>\n</div>\n<h2><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span><span id=\"2-Tang-truong-manh-me-tao-da-phat-trien-vuot-bac\" class=\"ez-toc-section\"></span>2. Tăng trưởng mạnh mẽ tạo đ&agrave; ph&aacute;t triển vượt bậc</h2>\n<p>Bước chuyển m&igrave;nh ấn tượng đ&atilde; mang đến đ&agrave; tăng trưởng mạnh mẽ trong những năm tiếp theo. Bệnh viện Đa khoa Quốc tế Thu C&uacute;c khi đ&oacute; đ&atilde; bắt tay ngay v&agrave;o giai đoạn 2 mở rộng hoạt động kinh doanh của bệnh viện. Cũng trong giai đoạn n&agrave;y, thương hiệu Thu C&uacute;c được t&aacute;ch độc lập 2 mảng hoạt động ch&iacute;nh của thương hiệu l&agrave; y tế kh&aacute;m chữa bệnh v&agrave; thẩm mỹ th&agrave;nh hai c&ocirc;ng ty ri&ecirc;ng biệt. C&ocirc;ng ty cổ phần y khoa Thu C&uacute;c bao gồm c&aacute;c bệnh viện đa khoa v&agrave; ph&ograve;ng kh&aacute;m đa khoa (Nay gọi tắt l&agrave; TCI). C&ocirc;ng ty thẩm mỹ Thu C&uacute;c được t&aacute;ch độc lập kh&ocirc;ng li&ecirc;n quan đến TCI bao gồm c&aacute;c bệnh viện phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m phẫu thuật thẩm mỹ, c&aacute;c ph&ograve;ng kh&aacute;m da liễu c&ocirc;ng nghệ cao. Mục ti&ecirc;u của thương hiệu Thu C&uacute;c l&agrave; để lĩnh vực y tế kh&aacute;m chữa bệnh ph&aacute;t triển mạnh mẽ hơn nữa, mang lại lợi &iacute;ch nhiều hơn cho người bệnh.</p>\n<p>Năm 2019, Bệnh viện ĐKQT Thu C&uacute;c &ndash; số 286-294 Thụy Khu&ecirc; được mở rộng quy m&ocirc; lớn gấp 3 lần, song song với việc khai trương Ph&ograve;ng kh&aacute;m Đa khoa Quốc tế Thu C&uacute;c tại 216 Trần Duy Hưng năm 2019. Tới th&aacute;ng 8/2020, sau 2 năm t&igrave;m hiểu, Quỹ Vinacapital ch&iacute;nh thức quyết định đầu tư v&agrave;o lĩnh vực y tế của Thu C&uacute;c, kh&ocirc;ng bao gồm thẩm mỹ. Đ&acirc;y l&agrave; một trong những bước đi chiến lược của Hệ thống trong giai đoạn ph&aacute;t triển thần tốc để trở th&agrave;nh một trong những thương hiệu dẫn đầu thị trường y tế tư nh&acirc;n.</p>\n<div>\n<div id=\"attachment_257933\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-257933 entered litespeed-loaded\" src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-3-1.jpg\" alt=\"Bệnh viện ĐKQT Thu C&uacute;c l&agrave; một trong những bệnh viện tư c&oacute; số lượng bệnh nh&acirc;n đến kh&aacute;m đ&ocirc;ng nhất tại H&agrave; Nội hiện nay.\" width=\"628\" height=\"415\" loading=\"lazy\" data-lazyloaded=\"1\" aria-describedby=\"caption-attachment-257933\" data-src=\"https://cdn.benhvienthucuc.vn/wp-content/uploads/2014/04/Anh-3-1.jpg\" data-ll-status=\"loaded\">\n<p id=\"caption-attachment-257933\" class=\"wp-caption-text\">Bệnh viện ĐKQT Thu C&uacute;c l&agrave; một trong những bệnh viện tư c&oacute; số lượng bệnh nh&acirc;n đến kh&aacute;m đ&ocirc;ng nhất tại H&agrave; Nội hiện nay.</p>\n</div>\n</div>\n<h2><span id=\"3-Nhung-chuyen-khoa-mui-nhon\" class=\"ez-toc-section\"></span><span id=\"3-Nhung-chuyen-khoa-mui-nhon\" class=\"ez-toc-section\"></span>3. Những chuy&ecirc;n khoa mũi nhọn</h2>\n<p>Để c&oacute; được th&agrave;nh quả đ&aacute;ng tự h&agrave;o h&ocirc;m nay, chất lượng kh&aacute;m chữa bệnh v&agrave; dịch vụ chăm s&oacute;c y tế tại Thu C&uacute;c TCI đ&oacute;ng g&oacute;p phần quan trọng nhất. Hoạt động theo m&ocirc; h&igrave;nh bệnh viện đa khoa với đội ngũ b&aacute;c sĩ chuy&ecirc;n khoa giỏi, Thu C&uacute;c TCI thực hiện thăm kh&aacute;m, điều trị với đủ c&aacute;c khoa, chuy&ecirc;n khoa. Ngo&agrave;i ung bướu, c&aacute;c mảng dịch vụ kh&aacute;m chữa bệnh mũi nhọn tại Thu C&uacute;c TCI hiện nay l&agrave; sản khoa, ngoại khoa, nội gan mật,&nbsp;<a href=\"https://benhvienthucuc.vn/giai-dap-chan-doan-hinh-anh-gom-nhung-gi/\">chẩn đo&aacute;n h&igrave;nh ảnh</a>. B&ecirc;n cạnh đ&oacute; Thu Cúc TCI cũng li&ecirc;n tục mở r&ocirc;̣ng các mảng dịch vụ khác như ti&ecirc;m chủng, kính mắt,&hellip;.</p>\n<p>Với Sản khoa, những yếu tố cảm x&uacute;c trong qu&aacute; tr&igrave;nh vượt cạn để gi&uacute;p mẹ bầu c&oacute; cuộc sinh nở tốt nhất đ&atilde; được chắt lọc từ nhiều nước ph&aacute;t triển v&agrave; &aacute;p dụng một c&aacute;ch tuyệt vời tại đ&acirc;y. Lựa chọn thai sản trọn g&oacute;i ở Thu C&uacute;c TCI, c&aacute;c bậc cha mẹ đ&atilde; lựa chọn một h&agrave;nh tr&igrave;nh của sự an t&acirc;m v&agrave; hạnh ph&uacute;c để gặp thi&ecirc;n thần b&eacute; bỏng của m&igrave;nh.</p>', 'Hòa Vang', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747821555/arfmiaypamkmsiokwob3.jpg', 'nguyễn chung chí', 'Thụy khê');

