--
-- Cấu trúc bảng cho bảng `chat`
--

CREATE TABLE `chat` (
  `id` bigint NOT NULL,
  `content` varchar(255) DEFAULT NULL,
  `created_date` datetime DEFAULT NULL,
  `file_name` varchar(255) DEFAULT NULL,
  `is_file` bit(1) DEFAULT NULL,
  `receiver` bigint DEFAULT NULL,
  `sender` bigint DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `doctors`
--

CREATE TABLE `doctors` (
  `id` bigint NOT NULL,
  `consultation_fee` double DEFAULT NULL,
  `degree` varchar(255) DEFAULT NULL,
  `description` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci,
  `experience_years` int DEFAULT NULL,
  `full_name` varchar(255) DEFAULT NULL,
  `gender` varchar(255) DEFAULT NULL,
  `center_id` bigint DEFAULT NULL,
  `specialty_id` bigint DEFAULT NULL,
  `user_id` bigint DEFAULT NULL,
  `position` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

--
-- Đang đổ dữ liệu cho bảng `doctors`
--

INSERT INTO `doctors` (`id`, `consultation_fee`, `degree`, `description`, `experience_years`, `full_name`, `gender`, `center_id`, `specialty_id`, `user_id`, `position`) VALUES
(1, 400000, 'Thạc sỹ', '<p>B&aacute;c sĩ Nguyễn Văn H&agrave;, chuy&ecirc;n gia sản phụ khoa với bằng Chuy&ecirc;n khoa II từ Đại học Y H&agrave; Nội, hiện l&agrave; Ph&oacute; Gi&aacute;m đốc&nbsp;<a href=\"https://benhvienthucuc.vn/\">Bệnh viện Đa khoa Quốc tế Thu C&uacute;c</a>&nbsp;TCI v&agrave; Trưởng khoa Phụ Sản. Với chuy&ecirc;n m&ocirc;n cao v&agrave; vị tr&iacute; l&atilde;nh đạo, b&aacute;c sĩ đ&atilde; c&oacute; nhiều đ&oacute;ng g&oacute;p quan trọng v&agrave;o việc n&acirc;ng cao chất lượng chăm s&oacute;c sức khỏe sản phụ khoa.</p>\n<h2>Qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c</h2>\n<p>&ndash; Chuy&ecirc;n gia Y tế tại Cộng h&ograve;a Angola: Tại đ&acirc;y b&aacute;c sĩ H&agrave; đ&atilde; l&agrave;m việc với tư c&aacute;ch l&agrave; chuy&ecirc;n gia y tế nhằm mở rộng kinh nghiệm trong lĩnh vực y khoa quốc tế.</p>\n<p>&ndash; B&aacute;c sĩ&nbsp;<a href=\"https://benhvienthucuc.vn/kham-san-phu-khoa-la-gi-nhung-dieu-cac-chi-em-nen-biet/\">sản phụ khoa</a>&nbsp;tại Bệnh viện Phụ sản Trung ương: B&aacute;c sĩ đ&atilde; trực tiếp tham gia kh&aacute;m chữa bệnh v&agrave; chăm s&oacute;c sức khỏe sản phụ, từ đ&oacute; t&iacute;ch lũy kinh nghiệm chuy&ecirc;n m&ocirc;n s&acirc;u rộng.</p>\n<p>&ndash; Ph&oacute; trưởng khoa&nbsp;<a href=\"https://benhvienthucuc.vn/qua-trinh-de-thuong-cua-me-bau-dien-ra-nhu-the-nao/\">đẻ</a>&nbsp;tại Bệnh viện Phụ sản Trung ương: B&aacute;c sĩ H&agrave; đ&atilde; ph&aacute;t huy chuy&ecirc;n m&ocirc;n trong lĩnh vực sản khoa, đảm nhận vai tr&ograve; quản l&yacute;, điều phối hoạt động của khoa, t&iacute;ch lũy kinh nghiệm xử l&yacute; c&aacute;c ca sinh kh&oacute;, quản l&yacute; v&agrave; đ&agrave;o tạo đội ngũ nh&acirc;n vi&ecirc;n y tế.</p>\n<p>&ndash; Hiện tại: b&aacute;c đang giữ chức vụ Ph&oacute; Gi&aacute;m đốc Bệnh viện Đa khoa Quốc tế Thu C&uacute;c TCI đồng thời giữ chức vụ Trưởng khoa Phụ Sản, trực tiếp điều trị c&aacute;c ca bệnh phức tạp, hoạch định chiến lược ph&aacute;t triển cho bệnh viện, đặc biệt trong lĩnh vực sản phụ khoa</p>\n<h2>Kinh nghiệm chuy&ecirc;n m&ocirc;n</h2>\n<p>B&aacute;c sĩ Nguyễn Văn H&agrave; c&oacute; gần 40 năm kinh nghiệm trong lĩnh vực sản phụ khoa. B&aacute;c đ&atilde; t&iacute;ch lũy được kiến thức v&agrave; kỹ năng to&agrave;n diện trong cả sản khoa v&agrave; phụ khoa, từ những ca th&ocirc;ng thường đến những trường hợp phức tạp.</p>\n<p>Trong lĩnh vực sản khoa, b&aacute;c sĩ H&agrave; th&agrave;nh thạo trong việc kh&aacute;m, theo d&otilde;i v&agrave; điều trị c&aacute;c ca sản thường cũng như sản bệnh l&yacute;. B&aacute;c đặc biệt nổi tiếng với khả năng xử l&yacute; c&aacute;c ca đẻ kh&oacute;, bao gồm ng&ocirc;i m&ocirc;ng, sử dụng gi&aacute;c h&uacute;t v&agrave; forceps. B&ecirc;n cạnh đ&oacute;, b&aacute;c sĩ H&agrave; c&ograve;n l&agrave; một phẫu thuật vi&ecirc;n t&agrave;i ba, c&oacute; khả năng thực hiện c&aacute;c ca mổ lấy thai phức tạp như&nbsp;<a href=\"https://benhvienthucuc.vn/nhung-dieu-can-luu-y-sau-de-mo-cac-me-can-biet/\">mổ đẻ</a>&nbsp;cũ nhiều lần, rau c&agrave;i răng lược, rau tiền đạo v&agrave; rau bong non.</p>\n<p>Về phụ khoa, b&aacute;c sĩ H&agrave; chuy&ecirc;n s&acirc;u trong chẩn đo&aacute;n, điều trị v&agrave; phẫu thuật c&aacute;c bệnh l&yacute; phụ khoa. B&aacute;c đặc biệt th&agrave;nh thạo trong lĩnh vực phẫu thuật nội soi, &aacute;p dụng những kỹ thuật ti&ecirc;n tiến để mang lại hiệu quả điều trị cao v&agrave; giảm thiểu x&acirc;m lấn cho người bệnh.</p>\n<p>Ngo&agrave;i c&ocirc;ng t&aacute;c l&acirc;m s&agrave;ng, b&aacute;c sĩ H&agrave; c&ograve;n t&iacute;ch cực tham gia v&agrave;o hoạt động đ&agrave;o tạo v&agrave; chuyển giao kỹ thuật. B&aacute;c thường xuy&ecirc;n giảng dạy về phẫu thuật sản phụ khoa cho c&aacute;c b&aacute;c sĩ chuy&ecirc;n khoa sau đại học v&agrave; chuyển giao kỹ thuật mổ mở,&nbsp;<a href=\"https://benhvienthucuc.vn/mo-noi-soi-la-gi/\">mổ nội soi</a>&nbsp;cho nhiều bệnh viện trong nước v&agrave; nước ngo&agrave;i như L&agrave;o, Campuchia, Angola,&hellip;</p>\n<p>Với khả năng xử l&yacute; t&igrave;nh huống nhanh nhạy v&agrave; đưa ra phương &aacute;n điều trị an to&agrave;n, kịp thời, b&aacute;c sĩ H&agrave; đ&atilde; khẳng định được vị tr&iacute; l&agrave; một trong những chuy&ecirc;n gia trong lĩnh vực sản phụ khoa tại Việt Nam.</p>', 5, 'Hoàng Thị Hoài', 'Nữ', 1, 2, 4, 'Phó Giám đốc Bệnh viện ĐKQT Thu Cúc TCI, Trưởng khoa Phụ Sản'),
(3, 500000, 'Tiến sỹ', '<p>Thầy thuốc ưu t&uacute;, b&aacute;c sĩ CKII L&ecirc; Ngọc Thương c&oacute; bề d&agrave;y kinh nghiệm hơn 30 năm c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n v&agrave; quản l&yacute; trong ng&agrave;nh y tế. B&aacute;c sĩ L&ecirc; Ngọc Thương tốt nghiệp Đại học Y H&agrave; Nội, chuy&ecirc;n ng&agrave;nh Đa khoa Ngoại &ndash; Sản v&agrave; tốt nghiệp B&aacute;c sĩ Chuy&ecirc;n khoa II chuy&ecirc;n ng&agrave;nh Chấn thương chỉnh h&igrave;nh.</p>\n<p>B&aacute;c sĩ CKII L&ecirc; Ngọc Thương được giới chuy&ecirc;n m&ocirc;n cũng như đ&ocirc;ng đảo người bệnh c&ocirc;ng nhận l&agrave; chuy&ecirc;n gia giỏi trong lĩnh vực Ngoại khoa, đặc biệt trong mảng Chấn thương chỉnh h&igrave;nh.</p>\n<p>Bằng kiến thức y khoa s&acirc;u rộng c&ugrave;ng kinh nghiệm nhiều năm trong nghề, b&aacute;c sĩ L&ecirc; Ngọc Thương đ&atilde; gi&uacute;p nhiều người chiến thắng bệnh tật. B&aacute;c sĩ lu&ocirc;n tận t&igrave;nh trong kh&aacute;m v&agrave; điều trị, đồng thời quan t&acirc;m theo d&otilde;i sức khỏe bệnh nh&acirc;n, ch&uacute; trọng &aacute;p dụng c&aacute;c phương ph&aacute;p điều trị hiện đại, tối ưu, quyết t&acirc;m chữa trị hiệu quả cho người bệnh. V&igrave; vậy, b&aacute;c sĩ Thương lu&ocirc;n được người bệnh v&agrave; gia đ&igrave;nh bệnh nh&acirc;n tr&acirc;n trọng, tin tưởng.&nbsp;</p>\n<p>B&ecirc;n cạnh c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n y tế, b&aacute;c sĩ L&ecirc; Ngọc Thương cũng thể hiện năng lực trong vai tr&ograve; nh&agrave; quản l&yacute; v&agrave; từng đảm nhận nhiều chức vụ quan trọng như:&nbsp;</p>\n<p>&ndash; Nguy&ecirc;n Trưởng khoa Ngoại, Ph&oacute; ph&ograve;ng Tổ chức c&aacute;n bộ, Ph&oacute; Gi&aacute;m đốc Bệnh viện Bưu điện.&nbsp;</p>\n<p>&ndash; Th&agrave;nh vi&ecirc;n hội đồng quản l&yacute;, Gi&aacute;m đốc Trung t&acirc;m điều dưỡng v&agrave; chăm s&oacute;c sức khỏe ng&agrave;nh Bưu điện.&nbsp;</p>\n<p>Với c&aacute;c th&agrave;nh t&iacute;ch đạt được cả về chuy&ecirc;n m&ocirc;n v&agrave; quản l&yacute;, b&aacute;c sĩ L&ecirc; Ngọc Thương vinh dự nhận danh hiệu Thầy thuốc ưu t&uacute; c&ugrave;ng nhiều bằng khen cao qu&yacute;.</p>', 7, 'Lê Ngọc Thương', 'Nam', 3, 3, 6, 'Phó Giám đốc Bệnh viện, Phụ trách Ngoại tổng hợp, kiêm Trưởng khoa Ngoại');

