--
-- Đang đổ dữ liệu cho bảng `services`
--

INSERT INTO `services` (`id`, `description`, `name`, `old_price`, `price`, `room`) VALUES
(1, '<h2 id=\"heading-3zyeu39z8\">1. X&eacute;t nghiệm m&aacute;u l&agrave; g&igrave;?</h2>\n<p>X&eacute;t nghiệm m&aacute;u l&agrave; c&aacute;c loại x&eacute;t nghiệm được thực hiện tr&ecirc;n c&aacute;c mẫu m&aacute;u được lấy v&agrave;o c&aacute;c ống chống đ&ocirc;ng kh&aacute;c nhau t&ugrave;y mục đ&iacute;ch x&eacute;t nghiệm, nhằm đo h&agrave;m lượng một số chất nhất định trong m&aacute;u hoặc đếm c&aacute;c loại tế b&agrave;o m&aacute;u kh&aacute;c nhau.&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/an-roi-co-xet-nghiem-mau-chinh-xac-nua-hay-khong-vi\"><strong>X&eacute;t nghiệm m&aacute;u</strong></a>&nbsp;c&oacute; thể được thực hiện để kiểm tra sức khỏe định kỳ, hỗ trợ chẩn đo&aacute;n bệnh hoặc t&igrave;m kiếm c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh, kiểm tra kh&aacute;ng thể hoặc s&agrave;ng lọc ung thư sớm nhờ c&aacute;c dấu hiệu của khối u (tumor marker) hoặc để đ&aacute;nh gi&aacute; hiệu quả của c&aacute;c phương ph&aacute;p điều trị.</p>\n<h2 id=\"heading-uuc64d40x\">2. X&eacute;t nghiệm m&aacute;u c&oacute; mấy loại?</h2>\n<h3 id=\"heading-uyd8q2px3\">2.1. X&eacute;t nghiệm c&ocirc;ng thức m&aacute;u to&agrave;n phần hay tổng ph&acirc;n t&iacute;ch tế b&agrave;o m&aacute;u (CBC - complete blood count)</h3>\n<p>X&eacute;t nghiệm c&ocirc;ng thức m&aacute;u to&agrave;n phần (CBC), hay&nbsp;<strong>tổng ph&acirc;n t&iacute;ch tế b&agrave;o m&aacute;u</strong>&nbsp;l&agrave; một trong những loại x&eacute;t nghiệm m&aacute;u phổ biến nhất. Trong c&aacute;c buổi kiểm tra sức khỏe định kỳ, kh&aacute;ch h&agrave;ng thường được y&ecirc;u cầu<a href=\"https://www.vinmec.com/vie/bai-viet/nhung-dieu-can-biet-ve-xet-nghiem-cong-thuc-mau-vi\"><strong>x&eacute;t nghiệm c&ocirc;ng thức m&aacute;u to&agrave;n phần</strong></a>.</p>\n<p>X&eacute;t nghiệm&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/tong-phan-tich-te-bao-mau-cbc-khi-mang-thai-vi\"><strong>tổng ph&acirc;n t&iacute;ch tế b&agrave;o m&aacute;u</strong></a>&nbsp;c&oacute; thể gi&uacute;p x&aacute;c định t&igrave;nh trạng sức khỏe chung, để để chẩn đo&aacute;n hoặc theo d&otilde;i điều trị rất nhiều t&igrave;nh trạng bệnh l&yacute; kh&aacute;c nhau, s&agrave;ng lọc cũng như ph&aacute;t hiện c&aacute;c bệnh về m&aacute;u v&agrave; c&aacute;c rối loạn của cơ thể ảnh hưởng đến tế b&agrave;o m&aacute;u như thiếu m&aacute;u, nhiễm tr&ugrave;ng, vi&ecirc;m, rối loạn đ&ocirc;ng m&aacute;u, ung thư m&aacute;u v&agrave; rối loạn hệ miễn dịch. X&eacute;t nghiệm n&agrave;y. l&agrave; một nh&oacute;m c&aacute;c x&eacute;t nghiệm đ&aacute;nh gi&aacute; c&aacute;c tế b&agrave;o lưu th&ocirc;ng trong m&aacute;u, bao gồm tế b&agrave;o hồng cầu (RBCs), bạch cầu (WBCs) v&agrave; tiểu cầu (PLT).</p>\n<h3 id=\"heading-p3wx7whuq\">2.2. X&eacute;t nghiệm sinh h&oacute;a m&aacute;u</h3>\n<p><a href=\"https://www.vinmec.com/vie/bai-viet/y-nghia-cua-cac-chi-so-xet-nghiem-sinh-hoa-mau-vi\"><strong>X&eacute;t nghiệm sinh h&oacute;a m&aacute;u</strong></a>&nbsp;bao gồm rất nhiều x&eacute;t nghiệm đo c&aacute;c chất kh&aacute;c nhau trong m&aacute;u. X&eacute;t nghiệm&nbsp;<strong>sinh h&oacute;a m&aacute;u</strong>&nbsp;thường được thực hiện tr&ecirc;n th&agrave;nh phần huyết tương hoặc huyết thanh của m&aacute;u. C&aacute;c x&eacute;t nghiệm c&oacute; thể cung cấp cho b&aacute;c sĩ th&ocirc;ng tin để đ&aacute;nh gi&aacute; chức năng thận v&agrave; gan, khối lượng v&agrave; t&igrave;nh trạng cơ của bạn (bao gồm cả tim), khớp v&agrave; c&aacute;c cơ quan kh&aacute;c.</p>\n<p>X&eacute;t nghiệm sinh h&oacute;a m&aacute;u bao gồm x&eacute;t nghiệm đường huyết, canxi v&agrave; điện giải, mỡ m&aacute;u, nồng độ acid uric, t&igrave;nh trạng thiếu m&aacute;u thiếu sắt cũng như x&eacute;t nghiệm m&aacute;u để đ&aacute;nh gi&aacute; chức năng tim, gan, thận. Một số x&eacute;t nghiệm n&agrave;y y&ecirc;u cầu phải nhịn ăn trước khi x&eacute;t nghiệm.</p>\n<h2 id=\"heading-6m9ixgec9\">3. X&eacute;t nghiệm m&aacute;u để l&agrave;m g&igrave;?</h2>\n<p><a href=\"https://www.vinmec.com/vie/bai-viet/vi-sao-can-chu-dong-xet-nghiem-mau-tong-quat-de-phat-hien-benh-vi\"><strong>X&eacute;t nghiệm m&aacute;u</strong></a>&nbsp;để kiểm tra nh&oacute;m m&aacute;u v&agrave; ph&aacute;t hiện rất nhiều bệnh như:</p>\n<ul>\n<li><strong>Bệnh về m&aacute;u</strong></li>\n</ul>\n<p>X&eacute;t nghiệm&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/tam-quan-trong-cua-xet-nghiem-tong-phan-tich-te-bao-mau-vi\"><strong>tổng ph&acirc;n t&iacute;ch tế b&agrave;o m&aacute;u</strong></a>&nbsp;c&oacute; khả năng s&agrave;ng lọc cũng như ph&aacute;t hiện c&aacute;c bệnh về m&aacute;u v&agrave; c&aacute;c rối loạn li&ecirc;n quan đến th&agrave;nh phần trong m&aacute;u, chẳng hạn như thiếu m&aacute;u, vi&ecirc;m nhiễm, bệnh k&yacute; sinh tr&ugrave;ng, vấn đề đ&ocirc;ng m&aacute;u, ung thư m&aacute;u,... C&aacute;c bệnh l&yacute; n&agrave;y được b&aacute;c sĩ chẩn đo&aacute;n qua c&aacute;c th&ocirc;ng số x&eacute;t nghiệm m&aacute;u như:</p>\n<p>- Kiểm tra c&aacute;c&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/tai-sao-te-bao-hong-cau-bi-vo-thi-sgot-lai-tang-rat-cao-vi\"><strong>tế b&agrave;o hồng cầu</strong></a>: Mức hồng cầu bất thường c&oacute; khả năng l&agrave; dấu hiệu của t&igrave;nh trạng thiếu m&aacute;u, mất nước, xuất huyết hoặc c&aacute;c chứng rối loạn kh&aacute;c về hồng huyết cầu.</p>\n<p>- Kiểm tra c&aacute;c tế b&agrave;o bạch cầu:&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/so-luong-bach-cau-trong-co-la-bao-nhieu-vi\"><strong>Số lượng bạch cầu</strong></a>&nbsp;trở n&ecirc;n bất thường c&oacute; thể l&agrave; dấu hiệu của nhiễm tr&ugrave;ng, ung thư m&aacute;u hoặc rối loạn hệ miễn dịch.</p>\n<p>- Kiểm tra c&aacute;c tiểu cầu:&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/so-luong-tieu-cau-trong-co-bao-nhieu-la-binh-thuong-vi\"><strong>Mức tiểu cầu</strong></a>&nbsp;bất thường sẽ g&acirc;y ra rối loạn chảy m&aacute;u hoặc bệnh dễ tụ huyết khối.</p>\n<p>- Hemoglobin (Hb):&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/cac-yeu-lam-thay-doi-ket-qua-chi-so-xet-nghiem-hemoglobin-vi\"><strong>Mức hemoglobin</strong></a>&nbsp;bất thường c&oacute; thể l&agrave; dấu hiệu của bệnh thiếu m&aacute;u, thiếu m&aacute;u hồng cầu h&igrave;nh liềm, hội chứng thalassemia hoặc c&aacute;c rối loạn m&aacute;u kh&aacute;c. Nếu bệnh nh&acirc;n bị tiểu đường, lượng đường dư thừa trong m&aacute;u c&oacute; khả năng li&ecirc;n kết với hemoglobin v&agrave; dẫn đến tăng mức hemoglobin A1c (<a href=\"https://www.vinmec.com/vie/bai-viet/y-nghia-xet-nghiem-hba1c-trong-kiem-soat-glucose-o-benh-nhan-dai-thao-duong-va-tam-soat-som-benh-dai-thao-duong-vi\"><strong>HbA1c</strong></a>) m&agrave; kh&ocirc;ng do bệnh l&yacute; đ&aacute;i th&aacute;o đường.</p>\n<p>- Hematocrit (Hct):&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/be-42-thang-tuoi-bi-sot-xuat-huyet-xet-nghiem-mau-tieu-cau-87-hematocrit-382-dieu-tri-nhu-nao-vi\"><strong>Hematocrit</strong></a>&nbsp;cao c&oacute; nghĩa l&agrave; bạn đang bị mất nước. Mức hematocrit thấp c&oacute; khả năng l&agrave; dấu hiệu của thiếu m&aacute;u. Sự bất thường đối với chỉ số Hct cũng c&oacute; thể l&agrave; dấu hiệu của rối loạn về m&aacute;u hoặc tủy xương.</p>\n<p>- Thể t&iacute;ch trung b&igrave;nh của hồng cầu (MCV): Mức MCV bất thường c&oacute; thể l&agrave; dấu hiệu của bệnh thiếu m&aacute;u n&oacute;i chung hoặc chứng thiếu m&aacute;u cục bộ.</p>\n<figure class=\"post-image full has-zoomable\"><picture><source srcset=\"\n                  /static/uploads/small_20190724_074659_340839_kiem_tra_gan_max_1800x1800_jpg_d24a3a1675.jpg\n                \" media=\"(max-width: 576px)\"><source srcset=\"\n                  /static/uploads/small_20190724_074659_340839_kiem_tra_gan_max_1800x1800_jpg_d24a3a1675.jpg\n                \" media=\"(max-width: 768px)\"><source srcset=\"\n                  /static/uploads/large_20190724_074659_340839_kiem_tra_gan_max_1800x1800_jpg_d24a3a1675.jpg\n                \" media=\"(min-width: 769px)\"><img class=\"full uploaded img-in-body\" src=\"https://www.vinmec.com/static/uploads/large_20190724_074659_340839_kiem_tra_gan_max_1800x1800_jpg_d24a3a1675.jpg\" alt=\"\nX&eacute;t nghiệm m&aacute;u gi&uacute;p kiểm tra chức năng của gan\n\" loading=\"lazy\" data-cfsrc=\"/static/uploads/large_20190724_074659_340839_kiem_tra_gan_max_1800x1800_jpg_d24a3a1675.jpg\" data-cfstyle=\"aspect-ratio:1200/628\"></picture>\n<figcaption class=\"caption\">\n<div class=\"rich-text\">X&eacute;t nghiệm m&aacute;u gi&uacute;p kiểm tra chức năng của gan</div>\n</figcaption>\n</figure>\n<ul>\n<li><strong>Kiểm tra chức năng của gan (SGOT, SGPT) v&agrave; chức năng thận</strong></li>\n</ul>\n<p>X&eacute;t nghiệm m&aacute;u gi&uacute;p&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/y-nghia-cua-cac-xet-nghiem-danh-gia-chuc-nang-vi\"><strong>đ&aacute;nh gi&aacute; chức năng thận</strong></a>&nbsp;sẽ thực hiện đo nồng độ ure m&aacute;u (BUN &ndash; Blood Ure Nitrogen) v&agrave; nồng độ creatinine m&aacute;u. Cả hai th&agrave;nh phần n&agrave;y đều l&agrave; những chất thận lọc ra khỏi cơ thể. Qu&aacute; tr&igrave;nh chuyển h&oacute;a chất đạm (protein) của cơ thể tạo ra sản phẩm cuối c&ugrave;ng l&agrave; ure m&aacute;u được đ&agrave;o thải ra ngo&agrave;i qua thận. Creatinin được đ&agrave;o thải qua thận v&agrave; thận duy tr&igrave; Creatinin trong m&aacute;u ở một nồng độ hằng định. Nếu kết quả x&eacute;t nghiệm cho thấy hai th&ocirc;ng số n&agrave;y bất thường th&igrave; rất c&oacute; thể l&agrave; dấu hiệu bệnh l&yacute; thận hoặc rối loạn chức năng thận hoặc c&aacute;c bệnh về gan như vi&ecirc;m gan A, B, C, E, D,.. xơ gan, tăng men gan, ung thư gan...</p>\n<div class=\"banner-block\">\n<div id=\"zone_18\" class=\"banner inpage-banner\"><iframe title=\"Internal Banner\" src=\"https://www.vinmec.com/banner/null\" loading=\"lazy\"></iframe></div>\n</div>\n<ul>\n<li><strong>Bệnh về đường huyết</strong></li>\n</ul>\n<p>X&eacute;t nghiệm m&aacute;u cho biết&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/ban-co-biet-luong-duong-trong-mau-binh-thuong-vi\"><strong>lượng đường (glucose)</strong></a>&nbsp;c&oacute; trong m&aacute;u của bạn. Đường huyết vượt qu&aacute; giới hạn c&oacute; thể l&agrave; dấu hiệu của bệnh đ&aacute;i th&aacute;o đường.</p>\n<p>Đối với x&eacute;t nghiệm m&aacute;u c&oacute; y&ecirc;u cầu đo glucose, b&aacute;c sĩ sẽ y&ecirc;u cầu người thực hiện phải nhịn ăn trước khi lấy m&aacute;u &iacute;t nhất từ 8 đến 12 giờ để đo đường huyết l&uacute;c đ&oacute;i. Ngo&agrave;i ra, một số x&eacute;t nghiệm đường huyết kh&aacute;c c&oacute; thể được thực hiện sau bữa ăn hoặc bất kỳ l&uacute;c n&agrave;o m&agrave; kh&ocirc;ng cần chuẩn bị trước (đường m&aacute;u sau ăn từ 1 đến 2 giờ hoặc đường m&aacute;u bất kỳ).</p>\n<ul>\n<li><strong>Rối loạn mỡ m&aacute;u (cholesterol, triglycerid, HDL-C)</strong></li>\n</ul>\n<p>X&eacute;t nghiệm m&aacute;u gi&uacute;p b&aacute;c sĩ x&aacute;c định được nguy cơ mắc bệnh tim mạch, bệnh l&yacute; mạch v&agrave;nh ở bệnh nh&acirc;n th&ocirc;ng qua c&aacute;c th&ocirc;ng số x&eacute;t nghiệm m&aacute;u li&ecirc;n quan đến&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/dau-hieu-nhan-biet-ban-bi-roi-loan-lipid-mau-vi\"><strong>lipid m&aacute;u</strong></a>&nbsp;(bao gồm hai loại ch&iacute;nh l&agrave; cholesterol v&agrave; triglyceride):</p>\n<ul>\n<li>Cholesterol (bao gồm hai th&agrave;nh phần ch&iacute;nh l&agrave; HDL-Cholesterol v&agrave; LDL-Cholesterol) trong đ&oacute; LDL-Cholesterol (<a href=\"https://www.vinmec.com/vie/bai-viet/phan-biet-cholesterol-xau-va-tot-vi\"><strong>cholesterol xấu</strong></a><strong>)</strong>: G&acirc;y ra tắc nghẽn trong l&ograve;ng mạch m&aacute;u, g&acirc;y xơ vữa động mạch. HDL-Cholesterol (<a href=\"https://www.vinmec.com/vie/bai-viet/11-loai-thuc-pham-giup-tang-cholesterol-tot-vi\"><strong>cholesterol tốt</strong></a><strong>)</strong>: L&agrave;m giảm t&igrave;nh trạng tắc nghẽn trong động mạch.</li>\n<li><a href=\"https://www.vinmec.com/vie/bai-viet/chi-so-triglyceride-la-gi-bao-nhieu-la-binh-thuong-vi\"><strong>Triglyceride</strong></a>: L&agrave; một loại chất b&eacute;o c&oacute; trong m&aacute;u. Khi bạn ăn, cơ thể sẽ chuyển h&oacute;a bất kỳ lượng calo n&agrave;o m&agrave; n&oacute; kh&ocirc;ng cần sử dụng ngay th&agrave;nh chất b&eacute;o trung t&iacute;nh v&agrave; được lưu trữ trong c&aacute;c tế b&agrave;o mỡ của bạn.</li>\n</ul>\n<p>Nồng độ cholesterol v&agrave; triglyceride bất thường cho thấy nguy cơ mắc bệnh tim mạch, bệnh l&yacute; mạch v&agrave;nh. Đối với x&eacute;t nghiệm để kiểm tra mỡ m&aacute;u, người thực hiện sẽ cần phải nhịn ăn từ 9 - 12 tiếng trước khi l&agrave;m x&eacute;t nghiệm để đảm bảo độ ch&iacute;nh x&aacute;c.</p>\n<ul>\n<li><strong>C&aacute;c bệnh li&ecirc;n quan đến hoạt động của enzym</strong></li>\n</ul>\n<p>C&aacute;c enzyme đ&oacute;ng vai tr&ograve; quan trọng hoạt động sống h&agrave;ng ng&agrave;y của cơ thể con người. Ch&uacute;ng li&ecirc;n kết v&agrave; biến đổi cấu tr&uacute;c của c&aacute;c ph&acirc;n tử nhằm phục vụ cho nhiều hoạt động kh&aacute;c nhau như h&ocirc; hấp, ti&ecirc;u h&oacute;a, chức năng cơ v&agrave; thần kinh, X&eacute;t nghiệm kiểm tra enzym trong m&aacute;u thường được sử dụng để hỗ trợ chẩn đo&aacute;n trong c&aacute;c bệnh l&yacute; về gan (men gan), bệnh l&yacute; về tim (men tim..)</p>\n<p>B&ecirc;n cạnh đ&oacute; x&eacute;t nghiệm m&aacute;u c&oacute; thể ph&aacute;t hiện bệnh&nbsp;<a href=\"https://www.vinmec.com/vie/benh/gout-gut-3052\"><strong>Gout</strong></a>, t&igrave;nh trạng nhiễm HIV của người bệnh, kiểm tra xem thuốc đang d&ugrave;ng c&oacute; đủ liều lượng v&agrave; t&aacute;c dụng kh&ocirc;ng v&agrave; c&aacute;c bệnh về n&atilde;o như thiếu m&aacute;u n&atilde;o, nhiễm tr&ugrave;ng n&atilde;o,...</p>\n<h2 id=\"heading-nf5ewiavr\">3. Quy tr&igrave;nh x&eacute;t nghiệm m&aacute;u</h2>\n<h3 id=\"heading-oht5lifb3\">3.1 Trước khi x&eacute;t nghiệm m&aacute;u</h3>\n<figure class=\"post-image full has-zoomable\"><picture><source srcset=\"\n                  /static/uploads/small_20190724_074813_768169_xet_nghiem_nhom_mau_max_1800x1800_jpg_2a841de4e7.jpg\n                \" media=\"(max-width: 576px)\"><source srcset=\"\n                  /static/uploads/small_20190724_074813_768169_xet_nghiem_nhom_mau_max_1800x1800_jpg_2a841de4e7.jpg\n                \" media=\"(max-width: 768px)\"><source srcset=\"\n                  /static/uploads/small_20190724_074813_768169_xet_nghiem_nhom_mau_max_1800x1800_jpg_2a841de4e7.jpg\n                \" media=\"(min-width: 769px)\"><img class=\"full uploaded img-in-body\" src=\"https://www.vinmec.com/static/uploads/small_20190724_074813_768169_xet_nghiem_nhom_mau_max_1800x1800_jpg_2a841de4e7.jpg\" alt=\"\nMột số loại x&eacute;t nghiệm m&aacute;u người bệnh phải tr&aacute;nh ăn trong tối đa 12 giờ\n\" loading=\"lazy\" data-cfsrc=\"/static/uploads/small_20190724_074813_768169_xet_nghiem_nhom_mau_max_1800x1800_jpg_2a841de4e7.jpg\" data-cfstyle=\"aspect-ratio:512/512\"></picture>\n<figcaption class=\"caption\">\n<div class=\"rich-text\">Một số loại x&eacute;t nghiệm m&aacute;u người bệnh phải tr&aacute;nh ăn trong tối đa 12 giờ</div>\n</figcaption>\n</figure>\n<p>Một số loại&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/an-roi-co-xet-nghiem-mau-chinh-xac-nua-hay-khong-vi\"><strong>x&eacute;t nghiệm m&aacute;u</strong></a>&nbsp;y&ecirc;u cầu người bệnh phải nhịn ăn trong tối đa 12 giờ trước khi lấy mẫu.</p>\n<p>B&aacute;c sĩ sẽ đưa ra c&aacute;c hướng dẫn cụ thể bạn cần tu&acirc;n theo trước khi l&agrave;m x&eacute;t nghiệm. T&ugrave;y thuộc v&agrave;o loại x&eacute;t nghiệm m&aacute;u, bạn phải tu&acirc;n thủ c&aacute;c hướng dẫn v&igrave; n&oacute; c&oacute; thể ảnh hưởng đến kết quả x&eacute;t nghiệm, do đ&oacute; việc x&eacute;t nghiệm c&oacute; thể phải tr&igrave; ho&atilde;n hoặc lặp lại:</p>\n<ul>\n<li>Tr&aacute;nh ăn hoặc uống (trừ nước lọc) trong tối đa 12 giờ.</li>\n<li>Nếu đang sử dụng một số loại thuốc hay thực phẩm chức năng, bạn sẽ được y&ecirc;u cầu dừng trong một khoảng thời gian nhất định trước khi lấy m&aacute;u.</li>\n</ul>\n<h3 id=\"heading-nfskqed7h\">3.2 Quy tr&igrave;nh x&eacute;t nghiệm m&aacute;u</h3>\n<p>X&eacute;t nghiệm&nbsp;<a href=\"https://www.vinmec.com/vie/bai-viet/tam-quan-trong-cua-xet-nghiem-tong-phan-tich-te-bao-mau-vi\"><strong>tổng ph&acirc;n t&iacute;ch tế b&agrave;o m&aacute;u</strong></a>&nbsp;hầu hết chỉ mất 5-10 ph&uacute;t để thực hiện kể từ sau khi nhận mẫu. C&aacute;c x&eacute;t nghiệm h&oacute;a sinh v&agrave; miễn dịch sẽ cần khoảng thời gian d&agrave;i hơn tuy nhi&ecirc;n trừ c&aacute;c x&eacute;t nghiệm rất phức tạp th&igrave; thời gian trả kết quả sẽ kh&ocirc;ng qu&aacute; từ một đến hai tiếng. Qu&aacute; tr&igrave;nh lấy m&aacute;u tĩnh mạch c&oacute; thể rất nhanh từ 5 đến 10 ph&uacute;t nếu tĩnh mạch người bệnh dễ d&agrave;ng nh&igrave;n thấy v&agrave; tiếp cận. X&eacute;t nghiệm m&aacute;u thường gồm lấy mẫu m&aacute;u từ mạch m&aacute;u ở c&aacute;nh tay. C&aacute;c mẫu m&aacute;u ở trẻ em thường được lấy từ đầu ng&oacute;n tay &aacute;p &uacute;t.</p>\n<ul>\n<li><strong>Bước 1:</strong>&nbsp;Người lấy mẫu garo c&aacute;nh tay người bệnh bằng một d&acirc;y quấn để l&agrave;m d&ograve;ng m&aacute;u chảy chậm lại v&agrave; l&agrave;m cho tĩnh mạch nổi r&otilde; hơn, gi&uacute;p việc lấy m&aacute;u được dễ d&agrave;ng.</li>\n<li><strong>Bước 2:</strong>&nbsp;Người lấy mẫu lau sạch v&ugrave;ng da khu vực chuẩn bị lấy m&aacute;u bằng chất khử tr&ugrave;ng trước khi lấy mẫu m&aacute;u.</li>\n<li><strong>Bước 3</strong>: Người lấy mẫu đưa một kim ti&ecirc;m gắn v&agrave;o ống ti&ecirc;m hoặc dụng cụ chứa mẫu đặc biệt v&agrave;o tĩnh mạch. Ống ti&ecirc;m được sử dụng để r&uacute;t mẫu m&aacute;u. Bạn c&oacute; thể cảm thấy ngứa hoặc ch&iacute;ch nhẹ khi kim đi v&agrave;o, nhưng kh&ocirc;ng g&acirc;y đau đớn.</li>\n<li><strong>Bước 4:</strong>&nbsp;Khi lấy mẫu xong, kim ti&ecirc;m sẽ được r&uacute;t ra. Người lấy mẫu &aacute;p một miếng b&ocirc;ng chặt tr&ecirc;n da một v&agrave;i ph&uacute;t.</li>\n<li><strong>Bước 5:</strong>&nbsp;D&ugrave;ng băng d&iacute;nh cứu thương d&aacute;n l&ecirc;n chỗ vừa lấy mẫu để đảm bảo v&ocirc; tr&ugrave;ng.</li>\n<li><strong>Bước 6:</strong> Sau khi lấy m&aacute;u, mẫu m&aacute;u được đưa v&agrave;o ống chứa mẫu c&oacute; d&aacute;n nh&atilde;n t&ecirc;n, ng&agrave;y th&aacute;ng năm sinh v&agrave; số định danh của bạn. Sau đ&oacute;, mẫu m&aacute;u được gửi đến ph&ograve;ng th&iacute; nghiệm để thực hiện tr&ecirc;n c&aacute;c hệ thống m&aacute;y tự động hoặc thủ c&ocirc;ng t&ugrave;y thuộc v&agrave;o y&ecirc;u cầu kiểm tra.</li>\n</ul>', 'Xét nghiệm máu', 1500000, 1200000, NULL),
(2, '<p>đ&acirc;y l&agrave; m&ocirc; tả chụp xquang</p>', 'Chụp xquang', NULL, 400000, NULL),
(3, '<p>đ&acirc;y l&agrave; m&ocirc; tả Nội soi dạ d&agrave;y</p>', 'Nội soi dạ dày', NULL, 700000, NULL),
(4, '<p>Đ&acirc;y l&agrave; m&ocirc; tả G&acirc;y m&ecirc; to&agrave;n th&acirc;n</p>', 'Gây mê toàn thân', NULL, 800000, NULL);

