--
-- Cấu trúc bảng cho bảng `specialty`
--

CREATE TABLE `specialty` (
  `id` bigint NOT NULL,
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci,
  `description` varchar(3000) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

--
-- Đang đổ dữ liệu cho bảng `specialty`
--

INSERT INTO `specialty` (`id`, `content`, `description`, `image`, `name`) VALUES
(1, '<p><a href=\"https://nhathuoclongchau.com.vn/bai-viet/kham-noi-tong-quat-la-gi-kham-noi-tong-quat-gom-nhung-gi.html\">Kh&aacute;m nội tổng qu&aacute;t</a>&nbsp;gi&uacute;p ph&aacute;t hiện c&aacute;c bất thường hoặc bệnh l&yacute; cần điều trị chuy&ecirc;n s&acirc;u. C&aacute;c bệnh l&yacute; nội khoa như&nbsp;<a href=\"https://nhathuoclongchau.com.vn/benh/benh-tim-mach-599.html\">bệnh tim mạch</a>, bệnh phổi, bệnh ti&ecirc;u h&oacute;a, tiểu đường, v&agrave; nhiều bệnh l&yacute; kh&aacute;c cần được ph&aacute;t hiện v&agrave; điều trị kịp thời.</p>\n<h2>Kh&aacute;m nội tổng qu&aacute;t l&agrave; g&igrave;?</h2>\n<p>Kh&aacute;m nội tổng qu&aacute;t hay c&ograve;n gọi l&agrave; kh&aacute;m nội, l&agrave; một phần quan trọng của việc theo d&otilde;i sức khỏe. Qu&aacute; tr&igrave;nh n&agrave;y bao gồm nhiều bước kh&aacute;m kh&aacute;c nhau để theo d&otilde;i t&igrave;nh trạng sức khỏe của một người. Thường, một cuộc kh&aacute;m nội tổng qu&aacute;t bao gồm:</p>\n<p><strong>Kiểm tra cơ bản:</strong>&nbsp;Đ&acirc;y bao gồm việc đo huyết &aacute;p, kiểm tra c&acirc;n nặng, v&agrave; đo chiều cao. Những th&ocirc;ng số n&agrave;y c&oacute; thể cung cấp th&ocirc;ng tin quan trọng về t&igrave;nh trạng cơ bản của cơ thể.</p>\n<p><strong>Kh&aacute;m l&acirc;m s&agrave;ng nội ti&ecirc;u h&oacute;a, h&ocirc; hấp, thần kinh, nội tiết, v&agrave; cơ xương khớp:&nbsp;</strong>C&aacute;c phần kh&aacute;m n&agrave;y nhằm kiểm tra t&igrave;nh trạng của c&aacute;c cơ quan v&agrave; hệ thống trong cơ thể. Ch&uacute;ng gi&uacute;p ph&aacute;t hiện sớm c&aacute;c vấn đề hoặc bệnh l&yacute; trong c&aacute;c lĩnh vực n&agrave;y.</p>\n<p><strong>X&eacute;t nghiệm v&agrave; tầm so&aacute;t nguy cơ ung thư:&nbsp;</strong>Một phần quan trọng trong cuộc kh&aacute;m n&agrave;y l&agrave; c&aacute;c x&eacute;t nghiệm m&aacute;u v&agrave; x&eacute;t nghiệm h&igrave;nh ảnh như tia X, si&ecirc;u &acirc;m, hoặc MRI để t&igrave;m kiếm dấu hiệu của bệnh l&yacute; tiềm ẩn hoặc nguy cơ&nbsp;<a href=\"https://nhathuoclongchau.com.vn/benh/ung-thu.html\">ung thư</a>.</p>\n<figure class=\"image\"><img src=\"https://cdn.nhathuoclongchau.com.vn/unsafe/https://cms-prod.s3-sgn09.fptcloud.com/kham_noi_tong_quat_la_gi_kham_noi_tong_quat_gom_nhung_gi_2f9fd06ce7.jpg\" alt=\"Kh&aacute;m nội tổng qu&aacute;t l&agrave; g&igrave;? Kh&aacute;m nội tổng qu&aacute;t gồm những g&igrave;?\">\n<figcaption><em>Kh&aacute;m nội tổng qu&aacute;t gi&uacute;p kiểm tra v&agrave; sớm ph&aacute;t hiện vấn đề sức khỏe b&ecirc;n trong cơ thể</em></figcaption>\n</figure>\n<p>Kh&aacute;m nội tổng qu&aacute;t chỉ l&agrave; một phần của qu&aacute; tr&igrave;nh kiểm tra sức khỏe tổng qu&aacute;t. Để tầm so&aacute;t v&agrave; ph&aacute;t hiện sớm c&aacute;c vấn đề y tế tiềm ẩn, việc thực hiện c&aacute;c x&eacute;t nghiệm l&acirc;m s&agrave;ng l&agrave; rất quan trọng. Kh&aacute;m nội tổng qu&aacute;t gi&uacute;p đ&aacute;nh gi&aacute; một c&aacute;ch chi tiết t&igrave;nh trạng sức khỏe của cơ thể v&agrave; gi&uacute;p đưa ra c&aacute;c phương &aacute;n điều trị hoặc thay đổi lối sống nếu cần thiết.</p>\n<h2>Kh&aacute;m nội tổng qu&aacute;t gồm những g&igrave;?</h2>\n<p>Việc tầm so&aacute;t v&agrave; ph&aacute;t hiện sớm bệnh l&yacute; qua c&aacute;c x&eacute;t nghiệm v&agrave; kh&aacute;m l&acirc;m s&agrave;ng l&agrave; một phần quan trọng trong qu&aacute; tr&igrave;nh duy tr&igrave; sức khỏe v&agrave; ngăn ngừa bệnh tật. Dưới đ&acirc;y l&agrave; quy tr&igrave;nh kh&aacute;m nội tổng qu&aacute;t cơ bản thường thực hiện để đ&aacute;nh gi&aacute; t&igrave;nh trạng sức khỏe:</p>\n<h3>Kh&aacute;m l&acirc;m s&agrave;ng</h3>\n<p>Bước đầu ti&ecirc;n của một cuộc kh&aacute;m tổng qu&aacute;t l&agrave; kh&aacute;m l&acirc;m s&agrave;ng. B&aacute;c sĩ nội tổng qu&aacute;t sẽ thực hiện kiểm tra tổng thể về t&igrave;nh trạng sức khỏe bằng c&aacute;ch đo chiều cao, c&acirc;n nặng, v&ograve;ng bụng, v&agrave; t&iacute;nh to&aacute;n&nbsp;<a href=\"https://nhathuoclongchau.com.vn/bai-viet/cach-tinh-chi-so-bmi-va-tieu-chuan-ve-suc-khoe-60210.html\">chỉ số BMI</a>&nbsp;(chỉ số khối cơ thể). Kiểm tra huyết &aacute;p, nhịp tim, v&agrave; nhịp thở để đ&aacute;nh gi&aacute; t&igrave;nh trạng thể lực của bệnh nh&acirc;n. Điều n&agrave;y gi&uacute;p ph&aacute;t hiện v&agrave; đ&aacute;nh gi&aacute; yếu tố nguy cơ tiềm ẩn cho c&aacute;c bệnh l&yacute;.</p>\n<h3>Kiểm tra tai, mũi, họng</h3>\n<p>Đối với kh&aacute;m nội tổng qu&aacute;t, b&aacute;c sĩ chuy&ecirc;n khoa tai mũi họng thực hiện kiểm tra v&agrave; tư vấn về sức khỏe của c&aacute;c cơ quan n&agrave;y. Điều n&agrave;y gi&uacute;p ph&aacute;t hiện c&aacute;c vấn đề như vi&ecirc;m họng,&nbsp;<a href=\"https://nhathuoclongchau.com.vn/benh/viem-amidan-467.html\">vi&ecirc;m amidan</a>, v&agrave; c&aacute;c bệnh l&yacute; tai mũi họng kh&aacute;c.</p>\n<h3>Kh&aacute;m răng h&agrave;m mặt</h3>\n<p>Kh&aacute;m nội tổng qu&aacute;t cũng bao gồm việc kiểm tra sức khỏe răng h&agrave;m mặt. B&aacute;c sĩ chuy&ecirc;n khoa nha khoa thực hiện kiểm tra, tư vấn về sức khỏe răng miệng, v&agrave; đề xuất điều trị hoặc biện ph&aacute;p duy tr&igrave;.</p>\n<h3>Kh&aacute;m cơ xương khớp</h3>\n<p>Đ&acirc;y l&agrave; một phần quan trọng trong việc ph&aacute;t hiện sớm bệnh l&yacute; về cơ xương khớp, nguy cơ tiềm t&agrave;ng, v&agrave; tư vấn về luyện tập cũng như việc n&acirc;ng cao thể lực.</p>\n<h3>Kh&aacute;m sản phụ khoa (cho phụ nữ)</h3>\n<p>Đối với nữ giới, cuộc kh&aacute;m nội tổng qu&aacute;t bao gồm kiểm tra v&agrave; ph&aacute;t hiện c&aacute;c bệnh về phụ khoa v&agrave; sản phụ khoa.</p>\n<p>Việc thực hiện kh&aacute;m nội tổng qu&aacute;t định kỳ nhằm theo d&otilde;i t&igrave;nh trạng sức khỏe tổng thể v&agrave; ph&aacute;t hiện sớm c&aacute;c vấn đề về sức khỏe. N&oacute; gi&uacute;p x&aacute;c định yếu tố nguy cơ, đ&aacute;nh gi&aacute; thể lực, v&agrave; đưa ra c&aacute;c chỉ định cụ thể cho từng người dựa tr&ecirc;n tuổi, giới t&iacute;nh, v&agrave; tiền sử bệnh.</p>\n<p>Ngo&agrave;i việc thực hiện kh&aacute;m nội tổng qu&aacute;t, sau đ&acirc;y l&agrave; một số x&eacute;t nghiệm v&agrave; quy tr&igrave;nh cận l&acirc;m s&agrave;ng quan trọng:</p>\n<h3>X&eacute;t nghiệm m&aacute;u</h3>\n<p><em>X&eacute;t nghiệm mỡ trong m&aacute;u:&nbsp;</em>Đ&aacute;nh gi&aacute; mức cholesterol v&agrave; triglycerides, yếu tố quan trọng li&ecirc;n quan đến sức khỏe tim mạch.</p>\n<p><em>X&eacute;t nghiệm đường (glucose) trong m&aacute;u:&nbsp;</em>Để kiểm tra mức đường trong m&aacute;u, quan trọng cho việc theo d&otilde;i tiềm ẩn về&nbsp;<a href=\"https://nhathuoclongchau.com.vn/benh/benh-tieu-duong-289.html\">bệnh tiểu đường.</a></p>\n<p><em>X&eacute;t nghiệm chức năng gan:&nbsp;</em>Đ&aacute;nh gi&aacute; sức khỏe gan bằng c&aacute;ch kiểm tra c&aacute;c&nbsp;<a href=\"https://nhathuoclongchau.com.vn/thanh-phan/enzymes\">enzymes</a>&nbsp;v&agrave; protein gan.</p>\n<p><em>X&eacute;t nghiệm chức năng thận:&nbsp;</em>Đ&aacute;nh gi&aacute; khả năng thận l&agrave;m việc bằng việc đo mức creatinine trong m&aacute;u, cho biết khả năng lọc m&aacute;u của thận.</p>\n<h3>X&eacute;t nghiệm nước tiểu</h3>\n<p>X&eacute;t nghiệm nước tiểu c&oacute; thể cung cấp th&ocirc;ng tin quan trọng về c&aacute;c chỉ số về đạm, đường, vi khuẩn. X&eacute;t nghiệm nước tiểu c&oacute; vai tr&ograve; quan trọng trong việc x&aacute;c định c&aacute;c vấn đề về thận, tiểu đường, v&agrave; một loạt c&aacute;c bệnh kh&aacute;c. Kết quả của x&eacute;t nghiệm gi&uacute;p b&aacute;c sĩ v&agrave; người bệnh hiểu r&otilde; hơn về t&igrave;nh trạng sức khỏe của m&igrave;nh v&agrave; đưa ra kế hoạch điều trị ph&ugrave; hợp.</p>\n<figure class=\"image\"><img src=\"https://cdn.nhathuoclongchau.com.vn/unsafe/https://cms-prod.s3-sgn09.fptcloud.com/kham_noi_tong_quat_la_gi_kham_noi_tong_quat_gom_nhung_gi_1_3bd1543837.jpg\" alt=\"Kh&aacute;m nội tổng qu&aacute;t l&agrave; g&igrave;? Kh&aacute;m nội tổng qu&aacute;t gồm những g&igrave;? 1\">\n<figcaption><em>X&eacute;t nghiệm m&aacute;u v&agrave; nước tiểu l&agrave; c&aacute;c kiểm tra kh&ocirc;ng thể thiếu trong quy tr&igrave;nh kh&aacute;m nội tổng qu&aacute;t</em></figcaption>\n</figure>\n<h3>Si&ecirc;u &acirc;m</h3>\n<p>Si&ecirc;u &acirc;m tim: Đ&aacute;nh gi&aacute; chức năng tim, kiểm tra van tim, v&agrave; xem x&eacute;t k&iacute;ch thước v&agrave; độ hở của c&aacute;c van.</p>\n<p>Si&ecirc;u &acirc;m bụng tổng qu&aacute;t: Đ&aacute;nh gi&aacute; t&igrave;nh trạng c&aacute;c cơ quan b&ecirc;n trong cơ thể như gan, t&uacute;i mật, thận, b&agrave;ng quang v&agrave; phần phụ.</p>\n<h3>Chụp X - quang</h3>\n<p>H&igrave;nh ảnh&nbsp;<a href=\"https://nhathuoclongchau.com.vn/bai-viet/chup-x-quang-phoi-va-tat-ca-nhung-dieu-can-biet.html\">chụp X - quang tim v&agrave; phổi</a>&nbsp;gi&uacute;p đ&aacute;nh gi&aacute; t&igrave;nh trạng sức khỏe tim v&agrave; phổi của cơ thể, ph&aacute;t hiện c&aacute;c vấn đề như vi&ecirc;m phổi hoặc vết thương.</p>\n<p>Những x&eacute;t nghiệm v&agrave; quy tr&igrave;nh n&agrave;y c&ugrave;ng với kh&aacute;m nội tổng qu&aacute;t gi&uacute;p đ&aacute;nh gi&aacute; sức khỏe tổng qu&aacute;t, ph&aacute;t hiện sớm c&aacute;c vấn đề y tế, v&agrave; x&aacute;c định liệu c&oacute; cần điều trị hoặc điều chỉnh lối sống để duy tr&igrave; sức khỏe tốt. Việc thực hiện định kỳ kiểm tra v&agrave; tầm so&aacute;t l&agrave; một phần quan trọng của việc duy tr&igrave; sức khỏe v&agrave; ph&ograve;ng ngừa bệnh tật.</p>\n<h2>B&aacute;c sĩ kh&aacute;m nội tổng qu&aacute;t l&agrave; ai?</h2>\n<p>B&aacute;c sĩ nội tổng qu&aacute;t ch&iacute;nh l&agrave; những chuy&ecirc;n gia Y Đa khoa với kiến thức tổng qu&aacute;t về nhiều kh&iacute;a cạnh của y học. C&oacute; nhiệm vụ chẩn đo&aacute;n v&agrave; điều trị c&aacute;c bệnh l&yacute; nội tiết,&nbsp;<a href=\"https://nhathuoclongchau.com.vn/benh/nhiem-trung-1027.html\">nhiễm tr&ugrave;ng</a>, h&ocirc; hấp, ti&ecirc;u h&oacute;a, tim mạch, thận, v&agrave; nhiều lĩnh vực kh&aacute;c.</p>\n<figure class=\"image\"><img src=\"https://cdn.nhathuoclongchau.com.vn/unsafe/800x0/https://cms-prod.s3-sgn09.fptcloud.com/kham_noi_tong_quat_la_gi_kham_noi_tong_quat_gom_nhung_gi_2_3d3c79fa26.jpg\" alt=\"Kh&aacute;m nội tổng qu&aacute;t l&agrave; g&igrave;? Kh&aacute;m nội tổng qu&aacute;t gồm những g&igrave;? 2\">\n<figcaption><em>B&aacute;c sĩ kh&aacute;m nội tổng qu&aacute;n l&agrave; những chuy&ecirc;n gia Y Đa khoa</em></figcaption>\n</figure>\n<p>B&aacute;c sĩ nội tổng qu&aacute;t cung cấp chăm s&oacute;c tổng qu&aacute;t cho người bệnh bao gồm: Theo d&otilde;i sức khỏe tổng thể, theo d&otilde;i c&aacute;c yếu tố như huyết &aacute;p, đường huyết, cholesterol, v&agrave; c&acirc;n nặng. Đề nghị c&aacute;c biện ph&aacute;p kiểm so&aacute;t bệnh l&yacute;, tư vấn về lối sống l&agrave;nh mạnh v&agrave; tiến h&agrave;nh kiểm tra định kỳ để theo d&otilde;i t&igrave;nh trạng sức khỏe của bệnh nh&acirc;n.</p>\n<p>Nếu c&oacute; bất kỳ triệu chứng hay kết quả x&eacute;t nghiệm đặc biệt, b&aacute;c sĩ nội tổng qu&aacute;t sẽ chỉ định v&agrave; chuyển hướng cho c&aacute;c chuy&ecirc;n gia chuy&ecirc;n ng&agrave;nh (như B&aacute;c sĩ Tim mạch, B&aacute;c sĩ H&ocirc; hấp, B&aacute;c sĩ Tai &ndash; Mũi &ndash; Họng) để tiến h&agrave;nh kh&aacute;m s&acirc;u v&agrave; điều trị chuy&ecirc;n m&ocirc;n.</p>', 'Khám nội tổng quát thường là nơi bắt đầu trong quá trình khám sức khỏe tổng quát và đóng một vai trò quan trọng trong việc đảm bảo tất cả các khía cạnh của sức khỏe của bạn đều được theo dõi và đánh giá một cách toàn diện. Vậy khám nội tổng quát là gì? Khám nội tổng quát gồm những gì?', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747750215/hirobr70z1qlhdv2i3gk.webp', 'Nội tổng quát'),
(2, '<h2 id=\"khoa-ngoai-tong-quat-la-gi\">Khoa ngoại tổng qu&aacute;t l&agrave; g&igrave;?</h2>\n<p>Khoa ngoại tổng qu&aacute;t l&agrave; một chuy&ecirc;n ng&agrave;nh y khoa chuy&ecirc;n về chẩn đo&aacute;n, điều trị bằng phẫu thuật nhiều cơ quan v&agrave; hệ thống trong cơ thể, chủ yếu bao gồm hệ ti&ecirc;u h&oacute;a (thực quản, dạ d&agrave;y, ruột non, ruột gi&agrave;, trực tr&agrave;ng, hậu m&ocirc;n), gan, mật, tụy, l&aacute;ch, th&agrave;nh bụng (v&iacute; dụ: tho&aacute;t vị), da v&agrave; m&ocirc; mềm, c&ugrave;ng với một số bệnh l&yacute; tuyến nội tiết như bệnh l&yacute; tuyến gi&aacute;p, tuyến cận gi&aacute;p v&agrave; tuyến thượng thận.. Đ&acirc;y l&agrave; một trong những chuy&ecirc;n khoa phổ biến nhất tại c&aacute;c bệnh viện, hỗ trợ điều trị từ những ca phẫu thuật đơn giản đến phức tạp.</p>\n<p>C&aacute;c lĩnh vực ch&iacute;nh trong kh&aacute;m khoa ngoại tổng qu&aacute;t gồm:</p>\n<ul>\n<li><strong>Phẫu thuật ti&ecirc;u h&oacute;a</strong>: Điều trị c&aacute;c bệnh về ruột non hoặc gi&agrave;, đại tr&agrave;ng, dạ d&agrave;y (bao tử) bằng phương ph&aacute;p nội soi hoặc phẫu thuật mở.</li>\n<li><strong>Phẫu thuật gan &ndash; mật &ndash; tụy</strong>: Giải quyết bệnh như vi&ecirc;m tụy cấp, sỏi.</li>\n<li><strong>Phẫu thuật tuyến gi&aacute;p</strong>: Can thiệp c&aacute;c bệnh về bướu gi&aacute;p, ung thư tuyến gi&aacute;p.</li>\n<li><strong>Phẫu thuật hậu m&ocirc;n &ndash; trực tr&agrave;ng</strong>: Điều trị trĩ, nứt hậu m&ocirc;n, r&ograve; hậu m&ocirc;n bằng phương ph&aacute;p x&acirc;m lấn tối thiểu hoặc cắt bỏ ho&agrave;n to&agrave;n.</li>\n</ul>\n<figure id=\"attachment_74134\" class=\"wp-caption alignnone\" aria-describedby=\"caption-attachment-74134\"><img class=\"size-full wp-image-74134\" src=\"https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1.jpg\" sizes=\"(max-width: 800px) 100vw, 800px\" srcset=\"https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1.jpg 800w, https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1-300x225.jpg 300w, https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1-768x576.jpg 768w, https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1-400x300.jpg 400w, https://cdn.diag.vn/2025/03/5580bc9f-ngoai-tong-quat-gom-nhung-benh-gi-1-700x525.jpg 700w\" alt=\"ngoại tổng qu&aacute;t gồm những bệnh g&igrave;\" width=\"800\" height=\"600\">\n<figcaption id=\"caption-attachment-74134\" class=\"wp-caption-text\"><em>Ngoại tổng qu&aacute;t li&ecirc;n quan đến hệ ti&ecirc;u h&oacute;a, nội tiết, gan, mật, tuyến tụy, hậu m&ocirc;n &ndash; trực tr&agrave;ng v&agrave; c&aacute;c cơ quan kh&aacute;c</em></figcaption>\n</figure>\n<p>B&aacute;c sĩ ngoại tổng qu&aacute;t l&agrave; người chuy&ecirc;n chẩn đo&aacute;n, điều trị bằng phẫu thuật v&agrave; theo d&otilde;i hậu phẫu cho bệnh nh&acirc;n mắc c&aacute;c bệnh cần can thiệp ngoại khoa. Đ&acirc;y l&agrave; một lĩnh vực quan trọng v&igrave; nhiều bệnh kh&ocirc;ng thể điều trị nội khoa m&agrave; bắt buộc phải phẫu thuật để loại bỏ tổn thương hoặc ngăn ngừa biến chứng nguy hiểm.</p>\n<h3 id=\"benh-he-tieu-hoa\">Bệnh hệ ti&ecirc;u h&oacute;a</h3>\n<ul>\n<li>Vi&ecirc;m ruột thừa: Đ&acirc;y l&agrave; t&igrave;nh trạng vi&ecirc;m cấp t&iacute;nh của ruột thừa, một cấu tr&uacute;c nhỏ h&igrave;nh ống gắn v&agrave;o đoạn đầu của ruột gi&agrave;. Khi l&ograve;ng ruột thừa bị tắc nghẽn, thường do sỏi ph&acirc;n (ph&acirc;n cứng), sự ph&igrave; đại của m&ocirc; bạch huyết, hoặc &iacute;t gặp hơn l&agrave; do khối u hay dị vật, vi khuẩn c&oacute; thể ph&aacute;t triển g&acirc;y vi&ecirc;m, sưng v&agrave; nhiễm tr&ugrave;ng, dẫn đến đau dữ dội thường ở v&ugrave;ng hố chậu phải.</li>\n<li>Tắc ruột: Xảy ra khi ruột bị chặn lại do d&iacute;nh, xoắn hoặc khối u, khiến thức ăn, dịch ti&ecirc;u h&oacute;a kh&ocirc;ng thể lưu th&ocirc;ng.</li>\n<li>Vi&ecirc;m ph&uacute;c mạc: Vi&ecirc;m ph&uacute;c mạc l&agrave; t&igrave;nh trạng nhiễm tr&ugrave;ng nghi&ecirc;m trọng trong khoang bụng, thường do thủng bao tử, vi&ecirc;m ruột thừa vỡ, tắc ruột hoại tử.</li>\n</ul>\n<h3 id=\"benh-gan-tui-mat-tuyen-tuy\">Bệnh gan &ndash; t&uacute;i mật &ndash; tuyến tụy</h3>\n<ul>\n<li>Sỏi mật: Sỏi h&igrave;nh th&agrave;nh trong t&uacute;i mật hoặc đường mật do sự kết tinh của c&aacute;c th&agrave;nh phần trong dịch mật, chủ yếu l&agrave;&nbsp;<a href=\"https://diag.vn/blog/lipid/cholesterol/\" target=\"_blank\" rel=\"noopener\"><strong>cholesterol</strong></a>&nbsp;(tạo th&agrave;nh sỏi cholesterol) hoặc bilirubin (th&agrave;nh phần của sắc tố mật, tạo th&agrave;nh sỏi sắc tố). Sỏi c&oacute; thể g&acirc;y tắc nghẽn, vi&ecirc;m t&uacute;i mật, vi&ecirc;m đường mật hoặc vi&ecirc;m tụy.</li>\n<li>Vi&ecirc;m tụy cấp: L&agrave; t&igrave;nh trạng vi&ecirc;m nhiễm cấp t&iacute;nh của tuyến tụy, thường do sỏi hoặc uống rượu nhiều.</li>\n</ul>\n<h3 id=\"benh-hau-mon-truc-trang\">Bệnh hậu m&ocirc;n &ndash; trực tr&agrave;ng</h3>\n<ul>\n<li>Trĩ, nứt hậu m&ocirc;n, r&ograve; hậu m&ocirc;n: Nếu bệnh nặng, cần phẫu thuật cắt trĩ hoặc điều trị bằng phương ph&aacute;p x&acirc;m lấn tối thiểu.</li>\n<li>Ung thư đại trực tr&agrave;ng: Cần&nbsp;<a href=\"https://diag.vn/product-category/xet-nghiem/\">x&eacute;t nghiệm</a>&nbsp;sinh thiết, nội soi, chụp CT để chẩn đo&aacute;n.</li>\n</ul>\n<p><strong>Xem th&ecirc;m</strong>:&nbsp;<a href=\"https://diag.vn/blog/general/kham-suc-khoe-tong-quat/\">Kh&aacute;m sức khỏe tổng qu&aacute;t</a></p>\n<h2 id=\"kham-ngoai-tong-quat-la-kham-nhung-gi\">Kh&aacute;m ngoại tổng qu&aacute;t l&agrave; kh&aacute;m những g&igrave;?</h2>\n<p>Kh&aacute;m ngoại tổng qu&aacute;t l&agrave; bước quan trọng gi&uacute;p b&aacute;c sĩ chẩn đo&aacute;n bệnh ngoại khoa, đ&aacute;nh gi&aacute; mức độ nghi&ecirc;m trọng v&agrave; x&aacute;c định phương ph&aacute;p điều trị ph&ugrave; hợp, c&oacute; thể l&agrave; điều trị nội khoa hoặc can thiệp phẫu thuật. Quy tr&igrave;nh kh&aacute;m ngoại tổng qu&aacute;t c&oacute; thể kh&aacute;c nhau t&ugrave;y v&agrave;o từng bệnh nh&acirc;n, quy tr&igrave;nh của từng cơ sở y tế nhưng thường bao gồm thăm kh&aacute;m l&acirc;m s&agrave;ng, x&eacute;t nghiệm cận l&acirc;m s&agrave;ng, chẩn đo&aacute;n h&igrave;nh ảnh v&agrave; đ&aacute;nh gi&aacute; tiền phẫu nếu cần phẫu thuật.</p>\n<h3 id=\"tham-kham-voi-bac-si-ngoai-khoa\">Thăm kh&aacute;m với b&aacute;c sĩ ngoại khoa</h3>\n<p>B&aacute;c sĩ sẽ bắt đầu bằng việc hỏi bệnh sử, t&igrave;m hiểu c&aacute;c triệu chứng hiện tại v&agrave; khai th&aacute;c tiền sử bệnh l&yacute; c&aacute; nh&acirc;n v&agrave; gia đ&igrave;nh để s&agrave;ng lọc nguy cơ bệnh tật. Đ&acirc;y l&agrave; một bước quan trọng gi&uacute;p b&aacute;c sĩ khoanh v&ugrave;ng nguy&ecirc;n nh&acirc;n v&agrave; quyết định chỉ định c&aacute;c x&eacute;t nghiệm ph&ugrave; hợp.</p>\n<p>Một số yếu tố quan trọng m&agrave; b&aacute;c sĩ sẽ đ&aacute;nh gi&aacute;:</p>\n<ul>\n<li><strong>Triệu chứng bệnh hiện tại</strong>: Đau bụng, rối loạn ti&ecirc;u h&oacute;a, chảy m&aacute;u ti&ecirc;u h&oacute;a, sụt c&acirc;n kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n&hellip;</li>\n<li><strong>Tiền sử bệnh c&aacute; nh&acirc;n</strong>: Đ&atilde; từng mắc c&aacute;c bệnh li&ecirc;n quan đến ngoại tổng qu&aacute;t hay chưa.</li>\n<li><strong>Tiền sử gia đ&igrave;nh</strong>: C&oacute; ai trong gia đ&igrave;nh mắc bệnh ung thư ti&ecirc;u h&oacute;a, tuyến gi&aacute;p,&nbsp;<a href=\"https://diag.vn/blog/diabetes/dai-thao-duong/\" target=\"_blank\" rel=\"noopener\"><strong>tiểu đường</strong></a>,&nbsp;<a href=\"https://diag.vn/blog/cardiovascular/benh-tim-mach/\" target=\"_blank\" rel=\"noopener\"><strong>bệnh tim mạch</strong></a>&hellip;hay kh&ocirc;ng.</li>\n</ul>\n<figure id=\"attachment_74133\" class=\"wp-caption alignnone\" aria-describedby=\"caption-attachment-74133\"><img class=\"size-full wp-image-74133\" src=\"https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2.jpg\" sizes=\"(max-width: 800px) 100vw, 800px\" srcset=\"https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2.jpg 800w, https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2-300x225.jpg 300w, https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2-768x576.jpg 768w, https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2-400x300.jpg 400w, https://cdn.diag.vn/2025/03/6eb00552-ngoai-tong-quat-gom-nhung-benh-gi-2-700x525.jpg 700w\" alt=\"ngoại tổng qu&aacute;t gồm những bệnh g&igrave;\" width=\"800\" height=\"600\">\n<figcaption id=\"caption-attachment-74133\" class=\"wp-caption-text\"><em>B&aacute;c sĩ sẽ bắt đầu bằng việc hỏi bệnh sử, t&igrave;m hiểu c&aacute;c triệu chứng hiện tại v&agrave; khai th&aacute;c tiền sử bệnh l&yacute; c&aacute; nh&acirc;n</em></figcaption>\n</figure>\n<h3 id=\"bac-si-chi-dinh-xet-nghiem-va-chan-doan-hinh-anh\">B&aacute;c sĩ chỉ định x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n h&igrave;nh ảnh</h3>\n<p>Dựa tr&ecirc;n t&igrave;nh trạng sức khỏe v&agrave; triệu chứng bệnh, b&aacute;c sĩ c&oacute; thể chỉ định thực hiện một số x&eacute;t nghiệm hoặc phương ph&aacute;p chẩn đo&aacute;n h&igrave;nh ảnh chuy&ecirc;n s&acirc;u nhằm x&aacute;c định nguy cơ bệnh v&agrave; mức độ tổn thương của cơ quan cần điều trị.</p>\n<ul>\n<li><strong>X&eacute;t nghiệm m&aacute;u v&agrave; x&eacute;t nghiệm chuy&ecirc;n biệt</strong>:&nbsp;<a href=\"https://diag.vn/blog/general/xet-nghiem-mau/\" target=\"_blank\" rel=\"noopener\"><strong>X&eacute;t nghiệm m&aacute;u</strong></a>&nbsp;v&agrave; x&eacute;t nghiệm chuy&ecirc;n biệt: T&ugrave;y thuộc v&agrave;o bệnh cảnh l&acirc;m s&agrave;ng, b&aacute;c sĩ c&oacute; thể chỉ định c&aacute;c x&eacute;t nghiệm m&aacute;u thường quy (c&ocirc;ng thức m&aacute;u, chức năng đ&ocirc;ng m&aacute;u, điện giải đồ&hellip;) v&agrave; c&aacute;c x&eacute;t nghiệm chuy&ecirc;n biệt hơn để đ&aacute;nh gi&aacute; chức năng c&aacute;c cơ quan li&ecirc;n quan (v&iacute; dụ:&nbsp;<a href=\"https://diag.vn/blog/liver/chuc-nang-cua-gan/\" target=\"_blank\" rel=\"noopener\"><strong>chức năng gan</strong></a>, thận, tụy), t&igrave;nh trạng nhiễm tr&ugrave;ng (v&iacute; dụ: CRP, bạch cầu), hoặc c&aacute;c dấu ấn đặc hiệu cho một số bệnh l&yacute; (v&iacute; dụ: x&eacute;t nghiệm vi khuẩn H. pylori trong bệnh l&yacute; dạ d&agrave;y).\n<ul>\n<li><a href=\"https://diag.vn/blog/general/xet-nghiem-sinh-hoa-mau/\" target=\"_blank\" rel=\"noopener\"><strong>X&eacute;t nghiệm sinh h&oacute;a m&aacute;u</strong></a>, nước tiểu để đ&aacute;nh gi&aacute; chức năng gan, thận, tim mạch.</li>\n<li>X&eacute;t nghiệm dấu ấn ung thư (AFP, CEA, CA 19-9, PSA) để ph&aacute;t hiện ung thư gan, đại tr&agrave;ng, tuyến tụy, tuyến tiền liệt.</li>\n<li>X&eacute;t nghiệm vi khuẩn HP để chẩn đo&aacute;n nhiễm khuẩn bao tử.</li>\n</ul>\n</li>\n<li><strong>Chụp MRI</strong>: MRI l&agrave; kỹ thuật h&igrave;nh ảnh cao cấp gi&uacute;p ph&aacute;t hiện ung thư to&agrave;n th&acirc;n, đ&aacute;nh gi&aacute; tổn thương n&atilde;o, tim mạch, thận, xương khớp&hellip; m&agrave; kh&ocirc;ng sử dụng tia X.</li>\n<li><strong>CT-scan</strong>: CT-Scan sử dụng tia X để dựng h&igrave;nh ảnh 3D, gi&uacute;p ph&aacute;t hiện khối u, chấn thương, vi&ecirc;m nhiễm v&agrave; tắc nghẽn trong c&aacute;c cơ quan nội tạng.</li>\n<li><strong>Chụp X-quang</strong>: Đ&acirc;y l&agrave; phương ph&aacute;p h&igrave;nh ảnh đơn giản, thường được chỉ định để đ&aacute;nh gi&aacute; hệ h&ocirc; hấp, ti&ecirc;u h&oacute;a, xương khớp.</li>\n<li><strong>Si&ecirc;u &acirc;m đa chiều</strong>: Si&ecirc;u &acirc;m l&agrave; phương ph&aacute;p kh&ocirc;ng x&acirc;m lấn, gi&uacute;p kiểm tra nhanh c&aacute;c vấn đề li&ecirc;n quan đến tim mạch, ti&ecirc;u h&oacute;a, gan, mật, tụy, thận.</li>\n<li><strong>Nội soi NBI</strong>: Nội soi NBI l&agrave; phương ph&aacute;p ti&ecirc;n tiến gi&uacute;p ph&aacute;t hiện ung thư v&ograve;m họng, bao tử, đại tr&agrave;ng sớm hơn so với nội soi th&ocirc;ng thường.</li>\n</ul>', 'Ngoại tổng quát là một chuyên khoa quan trọng trong y học, tập trung vào chẩn đoán và điều trị các bệnh lý cần can thiệp phẫu thuật liên quan đến nhiều cơ quan trong cơ thể như hệ tiêu hóa, đại tràng, tuyến giáp, tuyến tụy, túi mật, dạ dày, hậu môn – trực tràng. Vậy ngoại tổng quát gồm những bệnh gì? Hãy cùng giải đáp qua bài viết của Diag.', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747750269/xmzcenu87mgspbaczx0e.jpg', 'Ngoại tổng quát'),
(3, '<h2 id=\"nhung-dau-hieu-canh-bao-ve-benh-tim-mach-ban-nen-biet\" class=\"ftwp-heading\">Những dấu hiệu cảnh b&aacute;o về bệnh tim mạch bạn n&ecirc;n biết</h2>\n<p>C&aacute;c&nbsp;<a href=\"https://tamanhhospital.vn/nhoi-mau-co-tim-cap/\" target=\"_blank\" rel=\"noopener\">bệnh l&yacute; về tim mạch</a>&nbsp;nếu kh&ocirc;ng được can thiệp điều trị sớm, bệnh sẽ nặng dần theo thời gian, nguy cơ dẫn đến c&aacute;c biến chứng nguy hiểm, thậm ch&iacute; đe dọa t&iacute;nh mạng người bệnh. Do đ&oacute;, khi c&oacute; dấu hiệu nghi ngờ li&ecirc;n quan đến bệnh tim, bạn n&ecirc;n đến bệnh viện thăm kh&aacute;m, gi&uacute;p ph&aacute;t hiện v&agrave; điều trị bệnh sớm, đem lại hiệu quả chữa trị tốt v&agrave; tr&aacute;nh được c&aacute;c biến chứng. Những dấu hiệu cảnh b&aacute;o bệnh tim mạch m&agrave; bạn n&ecirc;n biết l&agrave;:</p>\n<ul>\n<li><a href=\"https://tamanhhospital.vn/kho-tho/\" target=\"_blank\" rel=\"noopener\">Kh&oacute; thở</a>: Xuất hiện khi người bệnh hoạt động gắng sức, khi nằm, kh&oacute; thở về đ&ecirc;m;</li>\n<li><a href=\"https://tamanhhospital.vn/dau-nguc/\" target=\"_blank\" rel=\"noopener\">Đau tức ở v&ugrave;ng ngực</a>: Cơn đau c&oacute; thể &acirc;m ỉ k&eacute;o d&agrave;i hoặc đau theo từng đợt, đau dữ dội t&ugrave;y theo nguy&ecirc;n nh&acirc;n bệnh l&yacute; ở tim. Đ&ocirc;i khi đau lan ra v&ugrave;ng bả vai, c&aacute;nh tay, cổ hoặc sau lưng;</li>\n<li><a href=\"https://tamanhhospital.vn/roi-loan-nhip-tim/\" target=\"_blank\" rel=\"noopener\">Rối loạn nhịp tim</a>: Tim đập nhanh/chậm bất thường, đập kh&ocirc;ng đều;</li>\n<li><a href=\"https://tamanhhospital.vn/hoi-hop-la-gi/\" target=\"_blank\" rel=\"noopener\">Hồi hộp</a>, đ&aacute;nh trống ngực, c&oacute; thể nghe được tiếng đập của tim;</li>\n<li>Bị sưng ph&ugrave; ở nhiều v&ugrave;ng tr&ecirc;n cơ thể, thường l&agrave; ở ch&acirc;n, mắt c&aacute; ch&acirc;n;</li>\n<li>Ch&oacute;ng mặt, ngất xỉu;</li>\n<li>Da, ni&ecirc;m mạc t&iacute;m lại ở m&ocirc;i, tay, ch&acirc;n hoặc nặng hơn l&agrave; to&agrave;n th&acirc;n sau khi hoạt động thể chất;</li>\n<li><a href=\"https://tamanhhospital.vn/tang-huyet-ap/\" target=\"_blank\" rel=\"noopener\">Huyết &aacute;p tăng</a>. (<a href=\"https://www.mayoclinic.org/diseases-conditions/heart-disease/symptoms-causes/syc-20353118\" target=\"_blank\" rel=\"nofollow noreferrer noopener\">1</a>)</li>\n</ul>\n<h2 id=\"viec-kham-suc-khoe-tim-mach-quan-trong-nhu-the-nao-doi-voi-benh-tim-mach\" class=\"ftwp-heading\">Việc kh&aacute;m sức khỏe tim mạch quan trọng như thế n&agrave;o đối với bệnh tim mạch?</h2>\n<p>Theo thống k&ecirc;, cứ 2 gi&acirc;y th&igrave; c&oacute; một bệnh nh&acirc;n tử vong v&igrave; c&aacute;c bệnh tim mạch, 5 gi&acirc;y đối với trường hợp bệnh nh&acirc;n bị&nbsp;<a href=\"https://tamanhhospital.vn/nhoi-mau-co-tim/\" target=\"_blank\" rel=\"noopener\">nhồi m&aacute;u cơ tim</a>&nbsp;v&agrave; 6 gi&acirc;y c&oacute; một bệnh nh&acirc;n tử vong do&nbsp;<a href=\"https://tamanhhospital.vn/dot-quy/\" target=\"_blank\" rel=\"noopener\">đột quỵ</a>.</p>\n<p>Hầu hết bệnh nh&acirc;n gặp c&aacute;c vấn đề về tim mạch ở giai đoạn đầu đều thường bỏ qua c&aacute;c triệu chứng của bệnh, v&igrave; ch&uacute;ng xuất hiện kh&ocirc;ng thường xuy&ecirc;n hoặc biểu hiện kh&ocirc;ng r&otilde;. Chỉ đến khi c&aacute;c dấu hiệu bệnh tim trở n&ecirc;n nặng hơn, ảnh hưởng đến hoạt động trong đời sống th&igrave; người bệnh mới đi thăm kh&aacute;m. Việc kh&aacute;m v&agrave; điều trị chậm trễ l&agrave; nguy&ecirc;n nh&acirc;n khiến bệnh tim c&agrave;ng trở n&ecirc;n nghi&ecirc;m trọng, chữa trị kh&oacute; v&agrave; hiệu quả thấp, c&oacute; nguy cơ g&acirc;y ra c&aacute;c biến chứng, thậm ch&iacute; nguy hiểm đến t&iacute;nh mạng người bệnh.</p>\n<p>Hiện nay, c&aacute;c bệnh l&yacute; tim mạch kh&ocirc;ng chỉ xuất hiện ở người cao tuổi, m&agrave; ng&agrave;y c&agrave;ng c&oacute; nhiều người trẻ mắc phải những bệnh n&agrave;y. Nguy&ecirc;n nh&acirc;n chủ yếu l&agrave; do lối sống thiếu khoa học của một bộ phận người trẻ hiện nay, lạm dụng c&aacute;c chất k&iacute;ch th&iacute;ch, căng thẳng, thường xuy&ecirc;n thức khuya, &iacute;t vận động, chế độ ăn kh&ocirc;ng c&acirc;n đối,&hellip; Điều n&agrave;y khiến rất nhiều bệnh nh&acirc;n ở độ tuổi 30 &ndash; 35, thậm ch&iacute; dưới 30 tuổi mắc phải c&aacute;c bệnh l&yacute; tim mạch v&agrave; tử vong do nhồi m&aacute;u cơ tim.</p>\n<p>Ch&iacute;nh v&igrave; vậy, việc kh&aacute;m sức khỏe tim mạch kh&ocirc;ng chỉ quan trọng đối với người cao tuổi, người c&oacute; dấu hiệu cảnh b&aacute;o bệnh tim, m&agrave; ngay cả những người trẻ tuổi khỏe mạnh cũng n&ecirc;n đi kh&aacute;m định kỳ để ph&aacute;t hiện v&agrave; điều trị bệnh sớm.</p>\n<figure id=\"attachment_121610\" class=\"wp-caption aligncenter\" aria-describedby=\"caption-attachment-121610\"><img class=\"size-full wp-image-121610\" src=\"https://tamanhhospital.vn/wp-content/uploads/2023/11/nguoi-tre-nen-kham-tim-mach-dinh-ky-de-tam-soat-benh.jpg\" alt=\"Người trẻ n&ecirc;n kh&aacute;m tim mạch định kỳ để tầm so&aacute;t bệnh\" width=\"847\" height=\"635\" loading=\"lazy\">\n<figcaption id=\"caption-attachment-121610\" class=\"wp-caption-text\">Người trẻ n&ecirc;n kh&aacute;m tim mạch định kỳ để tầm so&aacute;t bệnh</figcaption>\n</figure>\n<h2 id=\"khi-nao-can-di-kham-chuyen-khoa-tim-mach\" class=\"ftwp-heading\">Khi n&agrave;o cần đi kh&aacute;m chuy&ecirc;n khoa tim mạch?</h2>\n<h3 id=\"1-khi-duoc-chan-doan-cac-benh-ly-tim-mach-hoac-nguy-co-tim-mach-truoc-do\" class=\"ftwp-heading\">1. Khi được chẩn đo&aacute;n c&aacute;c bệnh l&yacute; tim mạch hoặc nguy cơ tim mạch trước đ&oacute;</h3>\n<p>Bệnh nh&acirc;n được chẩn đo&aacute;n c&oacute; nguy cơ mắc c&aacute;c bệnh l&yacute; tim mạch cần được kh&aacute;m chuy&ecirc;n khoa tim mạch. C&aacute;c x&eacute;t nghiệm y tế sẽ được chỉ định thực hiện t&ugrave;y thuộc v&agrave;o c&aacute;c triệu chứng của người bệnh, gi&uacute;p đưa ra chẩn đo&aacute;n ch&iacute;nh x&aacute;c về nguy&ecirc;n nh&acirc;n g&acirc;y bệnh.</p>\n<h3 id=\"2-khi-xuat-hien-cac-trieu-chung-co-nguy-co-ve-benh-tim-mach\" class=\"ftwp-heading\">2. Khi xuất hiện c&aacute;c triệu chứng c&oacute; nguy cơ về bệnh tim mạch</h3>\n<p>Người bệnh cần đi kh&aacute;m chuy&ecirc;n khoa tim mạch khi xuất hiện c&aacute;c triệu chứng cảnh b&aacute;o bệnh tim như: Đau ở ngực, kh&oacute; thở, ch&oacute;ng mặt, rối loạn nhịp tim, đ&aacute;nh trống ngực, đột ngột giảm thị lực, ngất xỉu,&hellip; Th&ocirc;ng qua c&aacute;c x&eacute;t nghiệm y học, b&aacute;c sĩ sẽ chẩn đo&aacute;n được c&aacute;c triệu chứng ở người bệnh c&oacute; li&ecirc;n quan đến những bất thường n&agrave;o ở tim để c&oacute; phương ph&aacute;p xử tr&iacute; ph&ugrave; hợp.</p>\n<blockquote>\n<p>&gt; Xem th&ecirc;m để biết c&aacute;ch chọn&nbsp;<strong><a href=\"https://tamanhhospital.vn/benh-vien-kham-tim-mach-o-tphcm/\" target=\"_blank\" rel=\"noopener\">bệnh viện kh&aacute;m tim mạch tốt ở TPHCM</a></strong>, uy t&iacute;n v&agrave; chuy&ecirc;n m&ocirc;n cao hiện nay.</p>\n</blockquote>\n<h3 id=\"3-kham-tim-mach-dinh-ky-neu-ban-co-cac-yeu-to-nguy-co-gay-benh-tim\" class=\"ftwp-heading\">3. Kh&aacute;m tim mạch định kỳ nếu bạn c&oacute; c&aacute;c yếu tố nguy cơ g&acirc;y bệnh tim</h3>\n<p>Kh&ocirc;ng phải đến khi n&agrave;o c&oacute; triệu chứng nghi ngờ bệnh tim th&igrave; mới cần đi kh&aacute;m chuy&ecirc;n khoa tim mạch. Tốt nhất bạn n&ecirc;n đi kh&aacute;m tim mạch định kỳ nếu c&oacute; c&aacute;c yếu tố nguy cơ g&acirc;y bệnh tim mạch như: Chế độ ăn thiếu khoa học, thường xuy&ecirc;n thức khuya, căng thẳng qu&aacute; mức, bị thừa c&acirc;n, b&eacute;o ph&igrave;, tăng huyết &aacute;p, đ&aacute;i th&aacute;o đường, rối loạn mỡ m&aacute;u, tiền sử gia đ&igrave;nh c&oacute; người mắc bệnh tim,&hellip;</p>\n<h2 id=\"kham-tim-mach-la-kham-nhung-gi-quy-trinh-va-phuong-phap-chan-doan\" class=\"ftwp-heading\">Kh&aacute;m tim mạch l&agrave; kh&aacute;m những g&igrave;? Quy tr&igrave;nh v&agrave; phương ph&aacute;p chẩn đo&aacute;n</h2>\n<h3 id=\"1-quy-trinh-kham-tim-mach-tai-benh-vien-da-khoa-tam-anh\" class=\"ftwp-heading\">1. Quy tr&igrave;nh kh&aacute;m tim mạch tại Bệnh viện Đa khoa T&acirc;m Anh</h3>\n<h4>Hỏi bệnh</h4>\n<p>Bệnh nh&acirc;n khi đi kh&aacute;m tim mạch, trước ti&ecirc;n sẽ được b&aacute;c sĩ thăm hỏi về t&igrave;nh trạng sức khỏe để c&oacute; đ&aacute;nh gi&aacute; ban đầu. B&aacute;c sĩ sẽ đưa ra một số c&acirc;u hỏi về c&aacute;c triệu chứng người bệnh gặp trước đ&oacute; c&oacute; li&ecirc;n quan đến bệnh tim như: đau ngực, kh&oacute; thở, ch&oacute;ng mặt, đ&aacute;nh trống ngực, ngất xỉu,&hellip;</p>\n<blockquote>\n<p><strong>Xem th&ecirc;m:</strong>&nbsp;C&aacute;c&nbsp;<strong><a href=\"https://tamanhhospital.vn/dau-hieu-benh-tim-o-nam-gioi/\" target=\"_blank\" rel=\"noopener\">dấu hiệu bệnh tim ở nam giới</a></strong>&nbsp;v&agrave; những&nbsp;<strong><a href=\"https://tamanhhospital.vn/dau-hieu-benh-tim-o-phu-nu/\" target=\"_blank\" rel=\"noopener\">dấu hiệu bệnh tim ở nữ giới</a></strong>&nbsp;c&oacute; v&agrave;i điểm kh&aacute;c nhau v&agrave; giống nhau, việc nhận biết sớm c&aacute;c dấu hiệu cảnh b&aacute;o gi&uacute;p người bệnh c&oacute; thể sắp xếp thời gian thăm kh&aacute;m sớm đề ph&ograve;ng ngừa tiến triển bệnh phức tạp hơn.</p>\n</blockquote>\n<p>Đồng thời, b&aacute;c sĩ cũng xem x&eacute;t về c&aacute;c yếu tố tiền sử gia đ&igrave;nh, c&aacute;c loại thuốc đang sử dụng nếu c&oacute;, thăm hỏi về lối sống, sinh hoạt hằng ng&agrave;y của bệnh nh&acirc;n. (<a href=\"https://www.ncbi.nlm.nih.gov/books/NBK2213/\" target=\"_blank\" rel=\"nofollow noreferrer noopener\">2</a>)</p>\n<figure id=\"attachment_121616\" class=\"wp-caption aligncenter\" aria-describedby=\"caption-attachment-121616\"><img class=\"size-full wp-image-121616\" src=\"https://tamanhhospital.vn/wp-content/uploads/2023/11/bac-si-pham-do-anh-thu-tham-hoi-suc-khoe-benh-nhan-de-danh-gia-ban-dau.jpg\" alt=\"ThS.BS Phạm Đỗ Anh Thư - Trung t&acirc;m Tim mạch, BVĐK T&acirc;m Anh TP.HCM thăm hỏi t&igrave;nh trạng sức khỏe của bệnh nh&acirc;n\" width=\"847\" height=\"565\" loading=\"lazy\">\n<figcaption id=\"caption-attachment-121616\" class=\"wp-caption-text\">ThS.BS Phạm Đỗ Anh Thư &ndash; Trung t&acirc;m Tim mạch, BVĐK T&acirc;m Anh TP.HCM thăm hỏi t&igrave;nh trạng sức khỏe của bệnh nh&acirc;n</figcaption>\n</figure>\n<h4>Kh&aacute;m thực thể đối với người bệnh</h4>\n<ul>\n<li><strong>Tư thế người bệnh:&nbsp;</strong>Bệnh nh&acirc;n nằm ngửa, ch&acirc;n hơi co, lưng v&agrave; đầu gối cao để b&aacute;c sĩ kiểm tra xem liệu c&oacute; bất thường hoặc triệu chứng n&agrave;o xuất hiện hay kh&ocirc;ng.</li>\n<li><strong>Quan s&aacute;t bệnh nh&acirc;n:&nbsp;</strong>Quan s&aacute;t c&aacute;c điểm bất thường ở b&ecirc;n ngo&agrave;i của bệnh nh&acirc;n c&oacute; thể gi&uacute;p chẩn đo&aacute;n ban đầu về c&aacute;c bệnh l&yacute; li&ecirc;n quan đến tim mạch như: M&agrave;u sắc của da v&agrave; ni&ecirc;m mạc t&iacute;m t&aacute;i, lồng ngực c&oacute; biểu hiện biến dạng, tĩnh mạch ở cổ nổi, tuyến gi&aacute;p to ra, v&ugrave;ng thượng vị đập theo nhịp tim, ph&ugrave; ch&acirc;n,&hellip;</li>\n<li><strong>Trực tiếp kiểm tra:&nbsp;</strong>C&aacute;c bất thường ở mỏm tim c&oacute; thể được ph&aacute;t hiện khi b&aacute;c sĩ thực hiện kiểm tra bằng c&aacute;ch sờ trực tiếp. C&aacute;c dự đo&aacute;n gi&uacute;p khoanh v&ugrave;ng bệnh như: Tim bị đẩy sang một b&ecirc;n, mỏm tim đập kh&ocirc;ng r&otilde;, mỏm tim đập nhanh.</li>\n<li><strong>Nghe tim:&nbsp;</strong>Để c&oacute; được chẩn đo&aacute;n ch&iacute;nh x&aacute;c hơn về t&igrave;nh trạng của tim, b&aacute;c sĩ sẽ dựa v&agrave;o những biểu hiện của bệnh nh&acirc;n v&agrave; nghe được sự thay đổi tiếng tim, sự thay đổi nhịp tim, &acirc;m thổi, tiếng cọ m&agrave;ng tim. (<a href=\"https://www.msdmanuals.com/professional/cardiovascular-disorders/approach-to-the-cardiac-patient/cardiovascular-examination\" target=\"_blank\" rel=\"nofollow noreferrer noopener\">3</a>)</li>\n</ul>\n<p>B&aacute;c sĩ sẽ c&oacute; c&aacute;ch kh&aacute;m tim mạch kh&aacute;c nhau t&ugrave;y thuộc v&agrave;o t&igrave;nh trạng của từng bệnh nh&acirc;n v&agrave; qua c&aacute;c bước thăm kh&aacute;m thực thể sẽ gi&uacute;p b&aacute;c sĩ c&oacute; cơ sở đưa ra những nhận định sơ bộ ban đầu về t&igrave;nh trạng tim, từ đ&oacute; c&oacute; chỉ định thực hiện c&aacute;c x&eacute;t nghiệm kh&aacute;c để đưa ra kết luận ch&iacute;nh x&aacute;c hơn.</p>\n<blockquote>\n<p><strong>&gt;&gt; Tham khảo th&ecirc;m:</strong>&nbsp;G&oacute;i&nbsp;<a href=\"https://tamanhhospital.vn/kham-tim-mach-cho-tre-em/\" target=\"_blank\" rel=\"noopener\"><strong>kh&aacute;m tim mạch cho trẻ em</strong></a>&nbsp;v&agrave; người lớn tại BVĐK T&acirc;m Anh TP.HCM&nbsp;<strong><a href=\"https://tamanhhospital.vn/ra-mat-goi-kham-tim-mach-cho-nguoi-lon-tre-em/\" target=\"_blank\" rel=\"noopener\">tại đ&acirc;y</a></strong>.</p>\n</blockquote>\n<h3 id=\"2-cac-ky-thuat-chan-doan-xac-dinh-benh\" class=\"ftwp-heading\">2. C&aacute;c kỹ thuật chẩn đo&aacute;n x&aacute;c định bệnh</h3>\n<h4>X&eacute;t nghiệm m&aacute;u</h4>\n<p>X&eacute;t nghiệm m&aacute;u l&agrave; kh&acirc;u cần thiết trong c&aacute;c bước kh&aacute;m tim mạch tổng qu&aacute;t. Kết quả x&eacute;t nghiệm sẽ gi&uacute;p b&aacute;c sĩ c&oacute; thể đ&aacute;nh gi&aacute; được số lượng v&agrave; chất lượng của c&aacute;c tế b&agrave;o m&aacute;u. Đồng thời, x&eacute;t nghiệm m&aacute;u c&ograve;n gi&uacute;p đ&aacute;nh gi&aacute; chức năng gan, thận, đường m&aacute;u, c&aacute;c rối loạn chức năng chuyển h&oacute;a lipid&hellip;</p>\n<h4>Điện t&acirc;m đồ</h4>\n<p>Bệnh nh&acirc;n c&oacute; c&aacute;c biểu hiện như đ&aacute;nh trống ngực, rối loạn nhịp tim c&ugrave;ng c&aacute;c biểu hiện kh&aacute;c c&oacute; nghi ngờ li&ecirc;n quan đến tim đều được cho x&eacute;t nghiệm bằng điện t&acirc;m đồ. X&eacute;t nghiệm gi&uacute;p theo d&otilde;i hoạt động, tốc độ, nhịp điệu của tim. Kết quả được hiển thị bằng dạng s&oacute;ng tr&ecirc;n m&aacute;y t&iacute;nh hoặc tr&ecirc;n giấy, gi&uacute;p chẩn đo&aacute;n c&aacute;c bệnh l&yacute; ở tim như rối loạn nhịp tim, nhồi m&aacute;u cơ tim, suy tim,&hellip;</p>\n<h4>Si&ecirc;u &acirc;m tim</h4>\n<p>Phương ph&aacute;p n&agrave;y d&ugrave;ng để khảo s&aacute;t c&aacute;c bất thường ở van tim, cơ tim, vi&ecirc;m nhiễm quanh van tim,&hellip; Si&ecirc;u &acirc;m tim gi&uacute;p b&aacute;c sĩ tiếp cận được những th&ocirc;ng tin quan trọng về cấu tr&uacute;c, hoạt động của tim. Đ&acirc;y l&agrave; kỹ thuật kh&ocirc;ng x&acirc;m lấn, th&ocirc;ng qua việc sử dụng s&oacute;ng si&ecirc;u &acirc;m tần số cao để c&oacute; được những h&igrave;nh ảnh động về tim, gi&uacute;p cho việc chẩn đo&aacute;n bệnh ch&iacute;nh x&aacute;c hơn.</p>\n<figure id=\"attachment_121619\" class=\"wp-caption aligncenter\" aria-describedby=\"caption-attachment-121619\"><img class=\"size-full wp-image-121619\" src=\"https://tamanhhospital.vn/wp-content/uploads/2023/11/sieu-am-tim-gang-suc-chan-doan-benh-ly-tim-mach.jpg\" alt=\"Si&ecirc;u &acirc;m tim gắng sức gi&uacute;p chẩn đo&aacute;n c&aacute;c bệnh l&yacute; về tim, đặc biệt l&agrave; t&igrave;nh trạng thiếu m&aacute;u cơ tim\" width=\"847\" height=\"565\" loading=\"lazy\">\n<figcaption id=\"caption-attachment-121619\" class=\"wp-caption-text\">Si&ecirc;u &acirc;m tim gắng sức gi&uacute;p chẩn đo&aacute;n c&aacute;c bệnh l&yacute; về tim, đặc biệt l&agrave; t&igrave;nh trạng thiếu m&aacute;u cơ tim</figcaption>\n</figure>\n<h4>X-quang ngực thẳng</h4>\n<p><a href=\"https://tamanhhospital.vn/thu-thuat-ky-thuat/chup-x-quang-nguc-thang/\" target=\"_blank\" rel=\"noopener\">Chụp X-quang ngực thẳng</a>&nbsp;được chỉ định thực hiện khi b&aacute;c sĩ c&oacute; nghi ngờ bệnh nh&acirc;n mắc bệnh về tim hoặc phổi.</p>\n<h4>Chụp CT mạch v&agrave;nh</h4>\n<p>Đ&acirc;y l&agrave; kỹ thuật gi&uacute;p x&aacute;c định vị tr&iacute; xuất ph&aacute;t, đường đi của mạch m&aacute;u nu&ocirc;i tim (hay c&ograve;n gọi l&agrave; mạch v&agrave;nh) v&agrave; đ&aacute;nh gi&aacute; sự tắc nghẽn (nếu c&oacute;), nhờ h&igrave;nh ảnh được ghi lại r&otilde; n&eacute;t, đa chiều hơn. Chụp CT mạch v&agrave;nh l&agrave; kỹ thuật hiệu quả để chẩn đo&aacute;n c&aacute;c bệnh l&yacute; tim mạch, đặc biệt l&agrave; bệnh động mạch v&agrave;nh.</p>', 'Khám tim mạch tổng quát đóng vai trò quan trọng trong việc giúp phát hiện sớm các bệnh lý về tim. Để từ đó, bác sĩ có thể đưa ra các phương pháp điều trị phù hợp, giúp cải thiện tình trạng bệnh và hạn chế các biến chứng. Vậy khi khám tim mạch tổng quát cần khám những gì? Quy trình như thế nào?', 'http://res.cloudinary.com/dlkhzppmb/image/upload/v1747750332/ngasfnsbvr7kl2lxeff4.jpg', 'Tim Mạch');

